import PsyanimBehaviorTreeNode from "./PsyanimBehaviorTreeNode.js";

export default class PsyanimBehaviorTree extends PsyanimBehaviorTreeNode {

    constructor(name = "root") {

        super(name);
    }

    printTree() {

        let nodeStack = [];

        nodeStack.push({ level: 0, node: this });

        while (nodeStack.length !== 0)
        {
            let nextNode = nodeStack.pop();

            let style = null;

            if (nextNode.node.status === PsyanimBehaviorTreeNode.STATUS.UNTICKED)
            {
                style = 'color: white;';
            }
            else if (nextNode.node.status === PsyanimBehaviorTreeNode.STATUS.FAILURE)
            {
                style = 'color: red;';
            }
            else if (nextNode.node.status === PsyanimBehaviorTreeNode.STATUS.SUCCESS)
            {
                style = 'color: green;';
            }
            else if (nextNode.node.status === PsyanimBehaviorTreeNode.STATUS.RUNNING)
            {
                style = 'color: yellow;';
            }
            else
            {
                console.error("Next node status is invalid!");
            }

            console.log('%c' + '|    '.repeat(nextNode.level) + '|-' + nextNode.node.name, style);

            for (let i = nextNode.node.children.length - 1; i >= 0; --i)
            {
                nodeStack.push({ level: nextNode.level + 1, node: nextNode.node.children[i] });
            }
        }
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../../PsyanimComponent.js';

import PsyanimBehaviorTree from './PsyanimBehaviorTree.js';
import PsyanimBehaviorTreeNode from './PsyanimBehaviorTreeNode.js';

export default class PsyanimBehaviorTreeAgent extends PsyanimComponent {

    constructor(entity) {

        super(entity);

        this._tree = null;
    }

    get tree() {

        return this._tree;
    }

    onEnable() {

        super.onEnable();
    }

    onDisable() {

        super.onDisable();
    }

    afterCreate() {

        super.afterCreate();

        this._tree = new PsyanimBehaviorTree();
    }

    beforeShutdown() {

        super.beforeShutdown();
    }

    onSensorEnter(entity) {

        super.onSensorEnter(entity);
    }

    onSensorExit(entity) {

        super.onSensorExit(entity);
    }

    update(t, dt) {
        
        super.update(t, dt);

        let status = this._tree.tick();

        if (status === PsyanimBehaviorTreeNode.STATUS.FAILURE ||
            status === PsyanimBehaviorTreeNode.STATUS.SUCCESS)
        {
            this._tree.reset();
        }
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../../PsyanimComponent.js';

import { PsyanimDebug } from 'psyanim-utils';

import PsyanimBehaviorTreeAgent from './PsyanimBehaviorTreeAgent.js';

export default class PsyanimBehaviorTreeDebugger extends PsyanimComponent {

    behaviorTreeAgentNames;

    constructor(entity) {

        super(entity);

        this.behaviorTreeAgentNames = [];

        this._keys = {
            Q: entity.scene.input.keyboard.addKey(Phaser.Input.Keyboard.KeyCodes.Q),
        };

        this._agentKeys = [];

        for (let i = 1; i < 11; ++i)
        {
            this._agentKeys.push('' + i);
        }

        this.scene.input.keyboard.on('keydown', this._handleKeyDownEvent.bind(this));
    }

    _handleKeyDownEvent(e) {

        if (e.key === 'q')
        {
            this.scene.resume();
        }

        for (let i = 0; i < this._behaviorTreeAgents.length; ++i)
        {
            let agent = this._behaviorTreeAgents[i];

            if (e.key === this._agentKeys[i])
            {
                this.scene.pause();

                console.clear();

                console.log('********************************');
                console.log('Agent name:', agent.entity.name);
                console.log('********************************');
                console.log('\n');

                agent.tree.printTree();
            }
        }
    }

    onEnable() {

        super.onEnable();
    }

    onDisable() {

        super.onDisable();
    }

    afterCreate() {

        super.afterCreate();

        if (this.behaviorTreeAgentNames.length > 10)
        {
            PsyanimDebug.error("PsyanimBehaviorTreeDebugger does not support more than 1 tree!");l
        }

        this._behaviorTreeAgents = [];

        this.behaviorTreeAgentNames.forEach(agentName => {

            let entity = this.entity.scene.getEntityByName(agentName);
            let agent = entity.getComponent(PsyanimBehaviorTreeAgent);

            this._behaviorTreeAgents.push(agent);
        });
    }

    beforeShutdown() {

        super.beforeShutdown();
    }

    onSensorEnter(entity) {

        super.onSensorEnter(entity);
    }

    onSensorExit(entity) {

        super.onSensorExit(entity);
    }

    update(t, dt) {
        
        super.update(t, dt);
    }
}

import PsyanimBehaviorTreeNode from "./PsyanimBehaviorTreeNode.js";

export default class PsyanimBehaviorTreeDecoratorNode extends PsyanimBehaviorTreeNode {

    tickDelegate;
    tickParameters;

    constructor(name, tickDelegate = null, tickParameters = null) {

        super(name);

        this.tickDelegate = tickDelegate;
        this.tickParameters = tickParameters;
    }

    tick() {
        
        if (this.tickDelegate)
        {
            let status = null;

            if (this.tickParameters)
            {
                status = this.tickDelegate(this.tickParameters);
            }
            else
            {
                status = this.tickDelegate();
            }

            this.validateTaskStatus(status);

            if (status === PsyanimBehaviorTreeNode.STATUS.SUCCESS)
            {
                let childStatus = this._children[0].tick();

                this.validateTaskStatus(childStatus);

                this._status = childStatus;
            }
            else
            {
                this._status = status;
            }
        }
        else
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.FAILURE;
        }

        return this._status;
    }
}

import PsyanimBehaviorTreeNode from "./PsyanimBehaviorTreeNode.js";

export default class PsyanimBehaviorTreeInverterNode extends PsyanimBehaviorTreeNode {

    constructor(name) {

        super(name);
    }

    tick() {
        
        let childStatus = this._children[0].tick();

        if (childStatus === PsyanimBehaviorTreeNode.STATUS.SUCCESS)
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.FAILURE;
        }
        else if (childStatus === PsyanimBehaviorTreeNode.STATUS.FAILURE)
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.SUCCESS;
        }
        else
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.RUNNING;
        }

        return this._status;
    }
}

