import { PsyanimDebug } from 'psyanim-utils';

export default class PsyanimBehaviorTreeNode {

    constructor(name) {

        this._name = name;
        this._children = [];
        this._status = PsyanimBehaviorTreeNode.STATUS.UNTICKED;
    }

    get name() {

        return this._name;
    }

    get status() {

        return this._status;
    }

    get children() {

        return this._children;
    }

    addChild(node) {

        this._children.push(node);
    }

    validateTaskStatus(status) {

        let isValid = (status === PsyanimBehaviorTreeNode.STATUS.UNTICKED ||
            status === PsyanimBehaviorTreeNode.STATUS.RUNNING ||
            status === PsyanimBehaviorTreeNode.STATUS.SUCCESS || 
            status === PsyanimBehaviorTreeNode.STATUS.FAILURE);

        if (!isValid)
        {
            PsyanimDebug.error('Invalid task status! node: ', this.name);
        }
    }

    reset() {

        this._status = PsyanimBehaviorTreeNode.STATUS.UNTICKED;
        this._children.forEach(child => child.reset());
    }

    tick() {

        if (this._children.length === 0)
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.FAILURE;
        }
        else
        {
            let childStatus = this._children[0].tick();

            this.validateTaskStatus(childStatus);
    
            this._status = childStatus;    
        }

        return this._status;
    }
}

const STATUS_ENUM = {
    UNTICKED: -1,
    FAILURE: 0x00,
    SUCCESS: 0x01,
    RUNNING: 0x02
};

PsyanimBehaviorTreeNode.STATUS = STATUS_ENUM;

import PsyanimBehaviorTreeNode from "./PsyanimBehaviorTreeNode.js";

export default class PsyanimBehaviorTreeSelectorNode extends PsyanimBehaviorTreeNode {

    constructor(name) {

        super(name);

        this._currentChildIndex = 0;
    }

    reset() {

        this._currentChildIndex = 0;

        super.reset();
    }

    tick() {
        
        let childStatus = this._children[this._currentChildIndex].tick();

        this.validateTaskStatus(childStatus);

        if (childStatus === PsyanimBehaviorTreeNode.STATUS.RUNNING)
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.RUNNING;
        }
        else if (childStatus === PsyanimBehaviorTreeNode.STATUS.SUCCESS)
        {
            this._currentChildIndex = 0;
            this._status = PsyanimBehaviorTreeNode.STATUS.SUCCESS;
        }
        else // child tick returned failure, let's move onto the next child if there are more
        {
            this._currentChildIndex++;

            if (this._currentChildIndex >= this._children.length)
            {
                this._currentChildIndex = 0;

                this._status = PsyanimBehaviorTreeNode.STATUS.FAILURE;
            }
            else
            {
                this._status = PsyanimBehaviorTreeNode.STATUS.RUNNING;
            }
        }

        return this._status;
    }
}

import PsyanimBehaviorTreeNode from "./PsyanimBehaviorTreeNode.js";

export default class PsyanimBehaviorTreeSequenceNode extends PsyanimBehaviorTreeNode {

    constructor(name) {

        super(name);

        this._currentChildIndex = 0;
    }

    reset() {

        this._currentChildIndex = 0;

        super.reset();
    }

    tick() {
        
        let childStatus = this._children[this._currentChildIndex].tick();

        this.validateTaskStatus(childStatus);

        if (childStatus === PsyanimBehaviorTreeNode.STATUS.RUNNING)
        {
            this._status = PsyanimBehaviorTreeNode.STATUS.RUNNING;
        }
        else if (childStatus  === PsyanimBehaviorTreeNode.STATUS.FAILURE)
        {
            this._currentChildIndex = 0;

            this._status = PsyanimBehaviorTreeNode.STATUS.FAILURE;
        }
        else // child tick returned succes, move onto next one if there are more
        {
            this._currentChildIndex++;

            if (this._currentChildIndex >= this._children.length)
            {
                this._currentChildIndex = 0;

                this._status = PsyanimBehaviorTreeNode.STATUS.SUCCESS;
            }
            else
            {
                this._status = PsyanimBehaviorTreeNode.STATUS.RUNNING;
            }
        }

        return this._status;
    }
}

import PsyanimFSMState from '../PsyanimFSMState.js';

import { PsyanimDebug } from 'psyanim-utils';

import PsyanimWanderBehavior from '../../steering/PsyanimWanderBehavior.js';

import PsyanimPlayfightChargeState from './PsyanimPlayfightChargeState.js';
import PsyanimVehicle from '../../steering/PsyanimVehicle.js';

export default class PsyanimPlayfightChargeDelayState extends PsyanimFSMState {

    averageDelay;
    delayVariance;

    constructor(fsm) {

        super(fsm);

        this.averageDelay = 600;
        this.delayVariance = 400;

        this._delay = this.averageDelay;

        this.fsm.setStateVariable('timer', 0);

        this.addTransition(PsyanimPlayfightChargeState, 'timer', (value) => value > this._delay);
    }

    _isTimerElapsed(timerValue) {

        return timerValue > this._delay;
    }

    afterCreate() {

        super.afterCreate();

        this._vehicle = this.entity.getComponent(PsyanimVehicle);
        this._wanderBehavior = this.entity.getComponent(PsyanimWanderBehavior);

        this._delay = this.averageDelay;
    }

    onPause() {

        super.onPause();
    }

    onStop() {

        super.onStop();
    }

    onResume() {

        super.onResume();
    }

    enter() {

        super.enter();

        this.fsm.setStateVariable('timer', 0);

        this._delay = (2 * Math.random() - 1) * this.delayVariance + this.averageDelay;

        if (this.fsm.debugGraphics)
        {
            this.entity.color = 0xfc9803;
        }
    }

    exit() {

        super.exit();
    }

    run(t, dt) {

        super.run(t, dt);

        // update state variables
        let updatedTimer = this.fsm.getStateVariable('timer') + dt;

        this.fsm.setStateVariable('timer', updatedTimer);

        // apply steering
        let steering = this._wanderBehavior.getSteering();

        this._vehicle.steer(steering);
    }
}

import PsyanimFSMState from '../PsyanimFSMState.js';

import { PsyanimDebug } from 'psyanim-utils';

import PsyanimVehicle from '../../steering/PsyanimVehicle.js';
import PsyanimArriveBehavior from '../../steering/PsyanimArriveBehavior.js';

import PsyanimPlayfightWanderState from './PsyanimPlayfightWanderState.js'

export default class PsyanimPlayfightChargeState extends PsyanimFSMState {

    sensor;

    maxChargeDuration;

    constructor(fsm) {

        super(fsm);

        this.maxChargeDuration = 1500;

        this.fsm.setStateVariable('charge', false);
        this.fsm.setStateVariable('chargeTimer', 0);

        /**
         *  Setup transitions here
         */

        this.addTransition(PsyanimPlayfightWanderState, 'charge', (value) => value === false);
    }

    afterCreate() {

        super.afterCreate();
    }

    onPause() {

        super.onPause();
    }

    onStop() {

        super.onStop();
    }

    onResume() {

        super.onResume();

        if (this.fsm.debugGraphics)
        {
            this.entity.color = 0xff0000;
        }
    }

    setTarget(target) {

        if (target) 
        {
            this.sensor.events.on('triggerEnter', this._handleCollision.bind(this));

            this._target = target;
        }
        else
        {
            PsyanimDebug.error('target field is null!');
        }
    }

    _handleCollision(entity) {

        if  (entity.name === this._target.name)
        {
            this.fsm.setStateVariable('charge', false);
        }
    }

    enter() {

        super.enter();

        this._vehicle = this.entity.getComponent(PsyanimVehicle);
        this._arriveBehavior = this.entity.getComponent(PsyanimArriveBehavior);

        if (this.sensor.isIntersecting(this._target))
        {
            this.fsm.setStateVariable('charge', false);
        }
        else
        {
            this.fsm.setStateVariable('charge', true);
        }

        this.fsm.setStateVariable('chargeTimer', 0);

        if (this.fsm.debugGraphics)
        {
            this.entity.color = 0xff0000;
        }
    }

    exit() {

        super.exit();
    }

    run(t, dt) {

        super.run();

        // update charge timer and see if we've exceeded the max charge duration
        let updatedChargeTimer = this.fsm.getStateVariable('chargeTimer') + dt;

        this.fsm.setStateVariable('chargeTimer', updatedChargeTimer);

        if (updatedChargeTimer > this.maxChargeDuration)
        {
            this.fsm.setStateVariable('charge', false);
        }

        // apply steering
        let steering = this._arriveBehavior.getSteering(this._target);

        this._vehicle.steer(steering);
    }
}

import PsyanimFSMState from '../PsyanimFSMState.js';

import { PsyanimDebug } from 'psyanim-utils';

import PsyanimFleeBehavior from '../../steering/PsyanimFleeBehavior.js';
import PsyanimVehicle from '../../steering/PsyanimVehicle.js';

import PsyanimPlayfightFSM from './PsyanimPlayfightFSM.js';

import PsyanimPlayfightWanderState from './PsyanimPlayfightWanderState.js';
import PsyanimPlayfightChargeState from './PsyanimPlayfightChargeState.js';

export default class PsyanimPlayfightFleeState extends PsyanimFSMState {

    target;

    maxFleeDuration;

    maxSpeed;
    maxAcceleration;

    constructor(fsm) {

        super(fsm);

        this.maxFleeDuration = 1000;

        this.fsm.setStateVariable('fleeTimer', 0);
        this.fsm.setStateVariable('wander', false);

        /**
         *  Setup transitions here
         */
        this.addTransition(PsyanimPlayfightWanderState, 'fleeTimer', (value) => value > this.maxFleeDuration);
        this.addTransition(PsyanimPlayfightWanderState, 'wander', (value) => value === true);
    }

    afterCreate() {

        this._vehicle = this.entity.getComponent(PsyanimVehicle);
        this._fleeBehavior = this.entity.getComponent(PsyanimFleeBehavior);

        this._targetAgentChargeState = this.target.getComponent(PsyanimPlayfightFSM)
            .getState(PsyanimPlayfightChargeState);
    }

    onPause() {

        super.onPause();
    }

    onStop() {

        super.onStop();
    }

    onResume() {

        super.onResume();

        if (this.fsm.debugGraphics)
        {
            this.entity.color = 0x0000ff;
        }

        this._fleeBehavior.maxSpeed = this.maxSpeed;
        this._fleeBehavior.maxAcceleration = this.maxAcceleration;
    }

    enter() {

        super.enter();

        this.fsm.setStateVariable('fleeTimer', 0);
        this.fsm.setStateVariable('wander', false);

        this._fleeBehavior.maxSpeed = this.maxSpeed;
        this._fleeBehavior.maxAcceleration = this.maxAcceleration;

        if (this.fsm.debugGraphics)
        {
            this.entity.color = 0x0000ff;
        }
    }

    exit() {

        super.exit();
    }

    run(t, dt) {

        super.run();

        // update flee timer
        let updatedFleeTimer = this.fsm.getStateVariable('fleeTimer') + dt;

        this.fsm.setStateVariable('fleeTimer', updatedFleeTimer);

        if (!this._targetAgentChargeState.isActive)
        {
            this.fsm.setStateVariable('wander', true);
        }

        // apply steering
        let steering = this._fleeBehavior.getSteering(this.target);

        this._vehicle.steer(steering);
    }
}

import PsyanimConstants from '../../../PsyanimConstants.js';

import PsyanimVehicle from '../../steering/PsyanimVehicle.js';
import PsyanimArriveBehavior from '../../steering/PsyanimArriveBehavior.js';
import PsyanimSeekBehavior from '../../steering/PsyanimSeekBehavior.js';
import PsyanimWanderBehavior from '../../steering/PsyanimWanderBehavior.js';
import PsyanimFleeBehavior from '../../steering/PsyanimFleeBehavior.js';

import PsyanimFSM from '../PsyanimFSM.js';

import PsyanimPlayfightChargeState from './PsyanimPlayfightChargeState.js';
import PsyanimPlayfightChargeDelayState from './PsyanimPlayfightChargeDelayState.js';
import PsyanimPlayfightWanderState from './PsyanimPlayfightWanderState.js';
import PsyanimPlayfightFleeState from './PsyanimPlayfightFleeState.js';

import PsyanimSensor from '../../physics/PsyanimSensor.js';

/**
 *  `PsyanimPlayfightFSM` implements the `Playfight v2` algorithm.
 * 
 *  Members marked with the `[advanced]` tag typically don't need to be modified. Although they can be modified, doing so can require tuning of several other parameters.
 */
export default class PsyanimPlayfightFSM extends PsyanimFSM {

    /***********************************************************************************************/
    /********************************* Playfight FSM Parameters ************************************/
    /***********************************************************************************************/

    /**
     *  Required field specifies the target entity this agent will playfight with.
     *  @type {PsyanimEntity}
     */
    target;

    /***********************************************************************************************/
    /********************************** Wander State Parameters ************************************/
    /***********************************************************************************************/

    /**
     *  [Range: 500 - 50000 ]
     *  [Default: 2000 ]
     *  Average time, in milliseconds, the agents will wander before attacking target again.
     *  @type {Number}
     */
    breakDurationAverage; 

    /**
     *  [Range: 500 - 50000 ]
     *  [Default: 1000 ]
     *  Allowed variance in break duration.
     *  @type {Number}
     */
    breakDurationVariance;

    /**
     *  [***advanced***]
     *  [Range: 50 - 50000 ]
     *  [Default: 150 ]
     *  Minimum time, in milliseconds, the agent must remain in wander state before it can transition out.
     *  @type {Number}
     */
    minWanderDuration;

    /**
     *  [Range: 50 - 1000 ]
     *  [Default: 200 ]
     *  Minimum distance from target from which this agent will initiate a charge.
     *  @type {Number}
     */
    minTargetDistanceForCharge;

    /**
     *  [Range: 200 - 1000 ]
     *  [Default: 500 ]
     *  Maximum distance from target which this agent will initiate a charge.
     *  @type {Number}
     */
    maxTargetDistanceForCharge;

    /**
     *  [Range: true/false ]
     *  [Default: true ]
     *  Controls whether the agent can flee or charge from wander state if attacked.
     *  @type {boolean}
     */
    wanderFleeOrChargeWhenAttacked;

    /**
     *  [Range: 200 - 1000 ]
     *  [Default: 250 ]
     *  Distance from attacker which this agent will transition to flee or charge delay state, if allowed.
     *  @type {Number}
     */
    wanderPanicDistance;

    /**
     *  [Range: 0.0 - 1.0 ]
     *  [Default: 0.5 ]
     *  Probability that agent will flee vs. charge when attacker is within panicDistance.
     *  @type {Number}
     */
    wanderFleeRate;

    /**
     *  [Range: 500 - 50000 ]
     *  [Default: 600 ]
     *  Average time, in milliseconds, the agents will wander before charging.
     *  @type {Number}
     */
    averageChargeDelay;

    /**
     *  [Range: 50 - 50000 ]
     *  [Default: 400 ]
     *  Allowed variance in charge delay.
     *  @type {Number}
     */
    chargeDelayVariance;

    /**
     *  [***advanced***]
     *  [Default: 75 ]
     *  @type {Number}
     */
    wanderSensorRadiusPadding;

    /***********************************************************************************************/
    /*********************************** Flee State Parameters *************************************/
    /***********************************************************************************************/

    /**
     *  [Range: 500 - 50000 ]
     *  [Default: 500 ]
     *  Maximum time this agent can remain in flee state since entering.
     *  @type {Number}
     */
    maxFleeDuration;

    /***********************************************************************************************/
    /********************************** Charge State Parameters ************************************/
    /***********************************************************************************************/

    /**
     *  [Range: 500 - 50000 ]
     *  [Default: 1500 ]
     *  Maximum time the agent can stay in charge state.
     *  @type {Number}
     */
    maxChargeDuration;

    /***********************************************************************************************/
    /******************************** Arrive Behavior Parameters ***********************************/
    /***********************************************************************************************/

    /**
     *  [Range: 3 - 20 ]
     *  [Default: 9 ]
     *  Maximum speed at which this agent can charge at it's target.
     *  @type {Number}
     */
    maxChargeSpeed;

    /**
     *  [Range: 0.05 - 0.7 ]
     *  [Default: 0.4 ]
     *  Maximum acceleration this agent can reach when charging at it's target.
     *  @type {Number}
     */
    maxChargeAcceleration;

    /**
     *  [***advanced***]
     *  [Range: 5 - 500 ]
     *  [Default: 12 ]
     *  Distance, in px, from target which the agent will come to rest.
     *  @type {Number}
     */
    innerDecelerationRadius;

    /**
     *  [***advanced***]
     *  [Range: 5 - 500 ]
     *  [Default: 30 ]
     *  Distance, in px, from target which the agent will begin slowing down.
     *  @type {Number}
     */
    outerDecelerationRadius;

    /***********************************************************************************************/
    /******************************** Wander Behavior Parameters ***********************************/
    /***********************************************************************************************/

    /**
     *  [Range: 3 - 20 ]
     *  [Default: 4 ]
     *  Maximum speed at which the agent will wander.
     *  @type {Number}
     */
    maxWanderSpeed;

    /**
     *  [Range: 0.05 - 0.7 ]
     *  [Default: 0.2 ]
     *  Maximum acceleration the agent can attain during wander.
     *  @type {Number}
     */
    maxWanderAcceleration;

    /**
     *  [***advanced***]
     *  [Range: 5 - 500 ]
     *  [Default: 50 ]
     *  Radius of the wander circle.
     *  @type {Number}
     */
    wanderRadius;

    /**
     *  [***advanced***]
     *  [Range: 5 - 500 ]
     *  [Default: 250 ]
     *  Distance the wander circle is offset from the agent's position.
     *  @type {Number}
     */
    wanderOffset;

