import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimAdvancedFleeBehavior extends PsyanimComponent {

    fleeBehavior;
    obstacleAvoidanceBehavior;

    sampleExpirationTime; // ms
    nSamplesForSmoothing;

    avoidanceSmoothingWindow; // ms

    constructor(entity) {

        super(entity);

        /**
         *  PsyanimAdvancedFleeBehavior is in a prototype state and really should be used
         *  with a pathfinder to get cleaner / more believable obstacle avoidance behavior.
         */

        this.sampleExpirationTime = 300;
        this.nSamplesForSmoothing = 10;

        this.avoidanceSmoothingWindow = 500;

        this._lastAvoidanceTime = 0;

        this._steeringSamples = [];
    }

    afterCreate() {

        super.afterCreate();
    }

    _addSteeringSample(steering)
    {
        if (this._steeringSamples.length >= this.nSamplesForSmoothing)
        {
            this._steeringSamples.shift();
        }

        this._steeringSamples.push({
            t: this.scene.time.now,
            steering: steering
        });
    }

    _getAverageSteeringValue() {

        let steering = new Phaser.Math.Vector2(0, 0);

        if (this._steeringSamples.length == 0)
        {
            return steering;
        }

        // compute the average value
        this._steeringSamples.forEach(sample => steering.add(sample.steering));

        if (steering.length() > this.obstacleAvoidanceBehavior.maxSeekAcceleration)
        {
            steering.setLength(this.obstacleAvoidanceBehavior.maxSeekAcceleration);
        }

        return steering;
    }

    _removeExpiredSamples() {

        if (this._steeringSamples.length == 0)
        {
            return;
        }
        
        let mostRecentSample = this._steeringSamples.at(-1);

        this._steeringSamples = this._steeringSamples
            .filter(s => mostRecentSample.t - s.t < this.sampleExpirationTime);
    }

    getSteering(target) {

        this._removeExpiredSamples();

        let steering = this.obstacleAvoidanceBehavior.getSteering();

        if (steering.length() > 1e-3)
        {
            this._lastAvoidanceTime = this.scene.time.now;
        }
        else
        {
            steering = this.fleeBehavior.getSteering(target);
        }

        let timeSinceLastAvoidance = this.scene.time.now - this._lastAvoidanceTime;

        if (timeSinceLastAvoidance < this.avoidanceSmoothingWindow)
        {
            this._addSteeringSample(steering);

            return this._getAverageSteeringValue();
        }
        else
        {
            return steering;
        }
    }
}

import PsyanimComponent from '../../PsyanimComponent.js';

import {
    PsyanimDebug
} from 'psyanim-utils';

export default class PsyanimAdvancedPlayfightBehavior extends PsyanimComponent {

    collisionFrequency;
    breakDuration;

    fleeBehavior;
    advancedArriveBehavior;
    wanderBehavior;

    debug;

    constructor(entity) {

        super(entity);

        this.debug = false;

        // TODO: can we just change 'collision frequency' & 'break duration' to seconds instead of ms?
        this.collisionFrequency = 2000;
        this.breakDuration = 1500;
    
        this._breakTimer = 0;

        this._setState(PsyanimAdvancedPlayfightBehavior.STATE.WANDERING);

        this._arriveTarget = this.entity.scene
            .addEntity(this.entity.name + '_arriveTarget', 0, 0, { isEmpty: true });
    }

    onEnable() {

        this.entity.disableFriction();
    }

    onDisable() {

        this.entity.enableFriction();
    }

    setTarget(target) {

        this._target = target;

        this.entity.setOnCollideWith(this._target.body, (matterCollisionData) => {
            this.handleCollision(matterCollisionData) 
        });
    }

    handleCollision(matterCollisionData) {

        if (this.debug)
        {
            PsyanimDebug.log("playfight collision occured at t = " + this.entity.scene.time.now / 1000);
        }

        this._breakTimer = 0;

        this._setState(PsyanimAdvancedPlayfightBehavior.STATE.FLEEING);    
    }

    get maxSpeed() {

        switch (this._state) {

            case PsyanimAdvancedPlayfightBehavior.STATE.CHARGING:

                return this.advancedArriveBehavior.maxSpeed;

            case PsyanimAdvancedPlayfightBehavior.STATE.WANDERING:

                return this.wanderBehavior.seekBehavior.maxSpeed;

            case PsyanimAdvancedPlayfightBehavior.STATE.FLEEING:

                return this.fleeBehavior.maxSpeed;
        }
    }

    get maxAcceleration() {

        switch (this._state) {

            case PsyanimAdvancedPlayfightBehavior.STATE.CHARGING:

                return this.advancedArriveBehavior.maxAcceleration;

            case PsyanimAdvancedPlayfightBehavior.STATE.WANDERING:

                return this.wanderBehavior.seekBehavior.maxAcceleration;

            case PsyanimAdvancedPlayfightBehavior.STATE.FLEEING:

                return this.fleeBehavior.maxAcceleration;
            }
    }

    _setState(state) {

        this._state = state;

        switch (this._state) {

            case PsyanimAdvancedPlayfightBehavior.STATE.CHARGING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: CHARGING');
                }

                this.entity.disableFriction();

                // move the arrive target to the midpoint between this.entity and this._target
                this._arriveTarget.position = this._target.position
                    .subtract(this.entity.position)
                    .scale(0.5)
                    .add(this.entity.position);

                // compute charge duration necessary to satisfy collision frequency
                this.advancedArriveBehavior.chargeDuration = (this.collisionFrequency - this._breakTimer) / 1000;

                if (this.debug)
                {
                    PsyanimDebug.log("charge duration = " + this.advancedArriveBehavior.chargeDuration);
                }

                // recompute max speed based on distance to target at start of charge
                this.advancedArriveBehavior.computeMaxSpeed(this._arriveTarget);

                break;

            case PsyanimAdvancedPlayfightBehavior.STATE.WANDERING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: WANDERING');
                }

                this.entity.enableFriction();

                break;

            case PsyanimAdvancedPlayfightBehavior.STATE.FLEEING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: FLEEING');
                }

                this.entity.enableFriction();

                break;
        }
    }

    updateBreakTimer(dt) {

        if (this._state == PsyanimAdvancedPlayfightBehavior.STATE.WANDERING)
        {
            this._breakTimer += dt;

            if (this._breakTimer >= this.breakDuration)
            {
                this._setState(PsyanimAdvancedPlayfightBehavior.STATE.CHARGING);
            }
        }
        else if (this._state == PsyanimAdvancedPlayfightBehavior.STATE.FLEEING)
        {
            this._breakTimer += dt;
        }
    }

    getSteering() {

        let distanceToTarget = 0;

        switch (this._state) {

            case PsyanimAdvancedPlayfightBehavior.STATE.CHARGING:

                return this.advancedArriveBehavior.getSteering(this._target);

            case PsyanimAdvancedPlayfightBehavior.STATE.WANDERING:

                distanceToTarget = this.entity.position
                    .subtract(this._target.position)
                    .length();

                if (distanceToTarget < this.fleeBehavior.panicDistance)
                {
                    // transition to fleeing and fall through to the next case
                    this._setState(PsyanimAdvancedPlayfightBehavior.STATE.FLEEING);
                }
                else
                {
                    return this.wanderBehavior.getSteering();
                }

            case PsyanimAdvancedPlayfightBehavior.STATE.FLEEING:

                distanceToTarget = this.entity.position
                    .subtract(this._target.position)
                    .length();

                if (distanceToTarget > this.fleeBehavior.panicDistance)
                {
                    this._setState(PsyanimAdvancedPlayfightBehavior.STATE.WANDERING);
                }

                return this.fleeBehavior.getSteering(this._target);
        }
    }
}

PsyanimAdvancedPlayfightBehavior.STATE = {
    WANDERING: 0x0001,
    CHARGING: 0x0002,
    FLEEING: 0x0004
};

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimArriveBehavior extends PsyanimComponent {

    maxSpeed;
    maxAcceleration;

    innerDecelerationRadius;
    outerDecelerationRadius;

    constructor(entity) {

        super(entity);

        this.maxSpeed = 5;
        this.maxAcceleration = 0.2;
    
        this.innerDecelerationRadius = 25;
        this.outerDecelerationRadius = 140;
    }

    getSteering(target) {

        let currentPosition = new Phaser.Math.Vector2(this.entity.x, this.entity.y);

        let targetRelativePosition = new Phaser.Math.Vector2(target.x, target.y);
        targetRelativePosition.subtract(currentPosition);

        let r = targetRelativePosition.length();

        let desiredSpeed = 0;

        let scaledMaxAcceleration = this.maxAcceleration;

        if (r <= this.innerDecelerationRadius)
        {
            this.entity.setVelocity(0, 0);

            return new Phaser.Math.Vector2(0, 0);
        }
        else if (r > this.outerDecelerationRadius)
        {
            desiredSpeed = this.maxSpeed;
        }
        else // ((r > this.innerDecelerationRadius) && r < this.r2)
        {
            //  v(r) = ((v_max) / (r2 - r1)) * (r - r1)
            desiredSpeed = ((this.maxSpeed) / (this.outerDecelerationRadius - this.innerDecelerationRadius)) * (r - this.innerDecelerationRadius);
            scaledMaxAcceleration = (r - this.innerDecelerationRadius) / (this.outerDecelerationRadius - this.innerDecelerationRadius) * this.maxAcceleration;
        }

        let desiredVelocity = targetRelativePosition.clone();

        desiredVelocity.setLength(desiredSpeed);

        let currentVelocityXY = this.entity.getVelocity();

        let currentVelocity = new Phaser.Math.Vector2(currentVelocityXY.x, currentVelocityXY.y);

        let acceleration = desiredVelocity.clone();
        acceleration.subtract(currentVelocity);

        if (acceleration.length() > scaledMaxAcceleration)
        {
            acceleration.setLength(scaledMaxAcceleration);
        }

        return acceleration;
    }
}

import PsyanimComponent from "../../PsyanimComponent.js";

import {
    PsyanimDebug
} from 'psyanim-utils';

export default class PsyanimBasicPredatorBehavior extends PsyanimComponent {

    /**
     *  Basic algorithm is to wander until a target is in sight, and then pursue that target, but
     *  more subtly by varying the pursuit angle a tad bit off from the normal direction an 
     *  'arrive' behavior would approach at.
     */

    subtlety; // 'degrees'
    subtletyLag; // 'ms'

    boredomDistance; // max. distance in 'px' to target beyond which this predator will no longer chase

    fovSensor;

    arriveBehavior;
    wanderBehavior;
    
    minimumWanderTime;

    debug;

    constructor(entity) {

        super(entity);

        this.debug = false;

        this.subtlety = 30; // 'degrees'
        this.subtletyLag = 500; // 'ms'
    
        this.minimumWanderTime = 1000;

        this._state = PsyanimBasicPredatorBehavior.STATE.WANDERING;

        this._arriveTarget = this.scene.addEntity(this.entity.name + '_arriveTarget');

        this._subtletyUpdateTimer = 0;

        this._wanderTimer = 0;
    }

    afterCreate() {

        super.afterCreate();

        // use an initial angle and subtlety that's randomized
        this.entity.setAngle(360 * (2 * Math.random() - 1));
        this._recomputeSubtletyAngle();
    }

    get maxSpeed() {

        switch(this._state) {

            case PsyanimBasicPredatorBehavior.STATE.WANDERING:

                return this.wanderBehavior.seekBehavior.maxSpeed;

            case PsyanimBasicPredatorBehavior.STATE.PURSUING:

                return this.arriveBehavior.maxSpeed;

            default:

                console.error("ERROR: invalid state: " + this._state);
                return 0;
        }
    }

    getSteering(target) {

        // determine if target is in sight
        let targetInSight = false;

        if (this.fovSensor)
        {
            let entitiesInSight = this.fovSensor.getEntitiesInSight([target]);

            if (entitiesInSight.length != 0)
            {
                if (entitiesInSight.includes(target))
                {
                    targetInSight = true;
                }
            }    
        }
        else // treat it as 360 degree FOV
        {
            targetInSight = true;
        }

        // update state
        if (targetInSight && this._wanderTimer > this.minimumWanderTime)
        {
            if (this._state != PsyanimBasicPredatorBehavior.STATE.PURSUING)
            {
                if (this.debug)
                {
                    PsyanimDebug.log("Predator '" + this.entity.name + "' state = PURSUING.");
                }
    
                this._state = PsyanimBasicPredatorBehavior.STATE.PURSUING;    
            }
        }
        else
        {
            if (this._state != PsyanimBasicPredatorBehavior.STATE.WANDERING)
            {
                if (this.debug)
                {
                    PsyanimDebug.log("Predator '" + this.entity.name + "' state = WANDERING.");
                }

                this._state = PsyanimBasicPredatorBehavior.STATE.WANDERING;
                this._wanderTimer = 0;
            }
        }

        // compute steering
        if (this._state == PsyanimBasicPredatorBehavior.STATE.PURSUING)
        {
            let targetRelativePosition = target.position
                .subtract(this.entity.position);

            targetRelativePosition.rotate(this._subtletyAngle * Math.PI / 180.0);

            let newTargetPosition = this.entity.position
                .add(targetRelativePosition);

            this._arriveTarget.position = newTargetPosition;

            return this.arriveBehavior.getSteering(this._arriveTarget);
        }
        else if (this._state == PsyanimBasicPredatorBehavior.STATE.WANDERING)
        {
            return this.wanderBehavior.getSteering();
        }
    }

    _recomputeSubtletyAngle() {

        this._subtletyAngle = this.subtlety * (2 * Math.random() - 1);
    }

    update(t, dt) {
     
        super.update(t, dt);

        if (this.fovSensor)
        {
            this.fovSensor.fovRange = this.boredomDistance;
        }

        this._wanderTimer += dt;

        this._subtletyUpdateTimer += dt;

        if (this._subtletyUpdateTimer > this.subtletyLag)
        {
            this._subtletyUpdateTimer = 0;

            if (this._state == PsyanimBasicPredatorBehavior.STATE.PURSUING)
            {
                this._recomputeSubtletyAngle();
            }
        }
    }
}

