import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';
import PsyanimConstants from '../../PsyanimConstants.js';
import PsyanimSensor from '../physics/PsyanimSensor.js';

export default class PsyanimCollisionAvoidanceBehavior extends PsyanimComponent {

    collisionRadius;
    maxAcceleration;

    constructor(entity) {

        super(entity);

        this.collisionRadius = 8;
        this.maxAcceleration = 0.2;    

        this._collisionAvoidanceTarget = null;

        this._nearbyAgents = [];

        this.sensor = this.entity.addComponent(PsyanimSensor);

        this.sensor.events.on('triggerEnter', (entity) => {

            this._nearbyAgents.push(entity);
        });

        this.sensor.events.on('triggerExit', (entity) => {

            this._nearbyAgents = this._nearbyAgents.filter(e => {
                e.name != entity.name;
            });
        });
    }

    setSensorRadius(radius) {

        this.sensor.setBody({
            shapeType: PsyanimConstants.SHAPE_TYPE.CIRCLE,
            radius: radius
        });
    }

    getSteering() {

        /**
         *  Algorithm here is lifted mostly straight from 'Artificial Inteligence for Games'
         *  by Ian Millington, 3rd ed.
         */

        if (this._nearbyAgents.length == 0)
        {
            this._collisionAvoidanceTarget = null;
            return new Phaser.Math.Vector2(0, 0);
        }

        let shortestTime = Infinity;
        let firstTarget = null;
        let firstMinSeparation = 0;
        let firstDistance = 0;
        let firstRelativePosition = null;
        let firstRelativeVelocity = null;

        // find target for collision avoidance
        for (let i = 0; i < this._nearbyAgents.length; ++i)
        {
            let agent = this._nearbyAgents[i];
            let relativePosition = agent.position.subtract(this.entity.position);
            let relativeVelocity = agent.velocity.subtract(this.entity.velocity);
            let relativeSpeed = relativeVelocity.length();

            let timeToCollision = -1 * relativePosition.dot(relativeVelocity) / (relativeSpeed * relativeSpeed);

            let distance = relativePosition.length();

            let minSeparation = relativeVelocity.clone()
                .scale(timeToCollision)
                .add(relativePosition)
                .length();

            if (minSeparation > 2 * this.collisionRadius)
            {
                continue;
            }

            if (timeToCollision > 0 && timeToCollision < shortestTime)
            {
                shortestTime = timeToCollision;
                firstTarget = agent;
                firstMinSeparation = minSeparation;
                firstDistance = distance;
                firstRelativePosition = relativePosition;
                firstRelativeVelocity = relativeVelocity;
            }
        }

        // calculate steering
        if (firstTarget == null)
        {
            this._collisionAvoidanceTarget = null;

            return new Phaser.Math.Vector2(0, 0);
        }

        let finalRelativePosition = null;

        if (firstMinSeparation <= 0 || firstDistance < 2 * this.collisionRadius)
        {
            finalRelativePosition = firstTarget.position.subtract(this.entity.position);
        }
        else
        {
            finalRelativePosition = firstRelativePosition.add(firstRelativeVelocity.scale(shortestTime));
        }

        this._collisionAvoidanceTarget = {
            target: firstTarget,
            finalRelativePosition: finalRelativePosition.clone()
        };

        finalRelativePosition.normalize();

        let steering = finalRelativePosition.scale(-1 * this.maxAcceleration);

        return steering;
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimEvadeBehavior extends PsyanimComponent {

    maxPredictionTime;

    fleeBehavior;

    constructor(entity) {

        super(entity);

        this.maxPredictionTime = 1.0;
    }

    getSteering(target) {

        let targetPosition = target.position;
        let distanceToTarget = this.entity.position.subtract(targetPosition).length();

        let targetVelocity = target.velocity;
        let targetSpeed = targetVelocity.length();

        let predictionTime = this.maxPredictionTime;

        if (targetSpeed > distanceToTarget / this.maxPredictionTime)
        {
            predictionTime = distanceToTarget / targetSpeed;

            // don't project too far out, predicted position can go past character.
            // Unity Movement AI package uses this magic number.  can adjust if needed.
            predictionTime *= 0.9;
        }

        let evadeTargetPosition = targetVelocity.clone();
        evadeTargetPosition.scale(predictionTime);
        evadeTargetPosition.add(targetPosition);

        let evadeTarget = {
            position: evadeTargetPosition,
        }

        return this.fleeBehavior.getSteering(evadeTarget);
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimFleeBehavior extends PsyanimComponent {

    maxSpeed;
    maxAcceleration;

    panicDistance;

    constructor(entity) {

        super(entity);

        this.maxSpeed = 6;
        this.maxAcceleration = 0.2;
    
        this.panicDistance = 250;    
    }

    getSteering(target) {

        let targetPosition = target.position;
        let distanceToTarget = this.entity.position.subtract(targetPosition).length();

        if (distanceToTarget > this.panicDistance)
        {
            return new Phaser.Math.Vector2(0, 0);
        }

        let desiredVelocity = new Phaser.Math.Vector2(targetPosition.x, targetPosition.y);
        desiredVelocity.subtract(this.entity.position);
        desiredVelocity.setLength(this.maxSpeed);
        desiredVelocity.scale(-1);

        let currentVelocityXY = this.entity.getVelocity();

        let currentVelocity = new Phaser.Math.Vector2(currentVelocityXY.x, currentVelocityXY.y);

        let acceleration = desiredVelocity.clone();
        acceleration.subtract(currentVelocity);

        if (acceleration.length() > this.maxAcceleration)
        {
            acceleration.setLength(this.maxAcceleration);
        }

        return acceleration;
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimObstacleAvoidanceBehavior extends PsyanimComponent {

    multiRaySensor;

    seekBehavior;
    maxSeekSpeed;
    maxSeekAcceleration;

    avoidDistance;

    constructor(entity) {

        super(entity);

        /**
         *  PsyanimObstacleAvoidanceBehavior should only be used on its own for avoiding 
         *  screen boundaries.  Use a pathfinder along with this for more complex geometry.
         */

        this.maxSeekSpeed = 5;
        this.maxSeekAcceleration = 0.2;
        this.avoidDistance = 25;

        this._seekTarget = this.scene.addEntity('_' + this.entity.name + '_seekTarget');
    }

    afterCreate() {

        super.afterCreate();
    }

    getSteering() {

        this.seekBehavior.maxSpeed = this.maxSeekSpeed;
        this.seekBehavior.maxAcceleration = this.maxSeekAcceleration;

        for (let ray of this.multiRaySensor.rayMap.values())
        {
            if (ray.collisions.length > 0)
            {
                let collision = ray.collisions[0];

                let targetPosition = new Phaser.Math.Vector2(
                    collision.point.x, collision.point.y)
                    .add(new Phaser.Math.Vector2(
                        collision.normal.x, collision.normal.y
                    ).scale(this.avoidDistance));

                this._seekTarget.position = targetPosition;

                return this.seekBehavior.getSteering(this._seekTarget);
            }
        }

        return Phaser.Math.Vector2.ZERO.clone();
    }

    _convertEntityAngleToVectorAngleD(entityAngleDegrees) {

        let newAngle = entityAngleDegrees;

        if (entityAngleDegrees < 0)
        {
            newAngle *= -1;
        }
        else
        {
            newAngle = 360 - newAngle;
        }

        return newAngle;
    }
}

import PsyanimComponent from '../../PsyanimComponent.js';

import {
    PsyanimDebug
} from 'psyanim-utils';

export default class PsyanimPlayfightBehavior extends PsyanimComponent {

    breakDuration;

    fleeBehavior;
    arriveBehavior;
    wanderBehavior;

    debug;

    constructor(entity) {

        super(entity);

        this.debug = false;

        this.breakDuration = 1500;

        this._breakTimer = 0;

        this._setState(PsyanimPlayfightBehavior.STATE.WANDERING);
    }

    handleCollision(matterCollisionData) {

        if (this.debug)
        {
            PsyanimDebug.log("playfight collision occured at t = " + this.entity.scene.time.now / 1000);
        }

        this._breakTimer = 0;

        this._setState(PsyanimPlayfightBehavior.STATE.FLEEING);    
    }

    get maxSpeed() {

        switch (this._state) {

            case PsyanimPlayfightBehavior.STATE.CHARGING:

                return this.arriveBehavior.maxSpeed;

            case PsyanimPlayfightBehavior.STATE.WANDERING:

                return this.wanderBehavior.seekBehavior.maxSpeed;

            case PsyanimPlayfightBehavior.STATE.FLEEING:

                return this.fleeBehavior.maxSpeed;
        }
    }

    get maxAcceleration() {

        switch (this._state) {

            case PsyanimPlayfightBehavior.STATE.CHARGING:

                return this.arriveBehavior.maxAcceleration;

            case PsyanimPlayfightBehavior.STATE.WANDERING:

                return this.wanderBehavior.seekBehavior.maxAcceleration;

            case PsyanimPlayfightBehavior.STATE.FLEEING:

                return this.fleeBehavior.maxAcceleration;
        }
    }

    _setState(state) {

        this._state = state;

        switch (this._state) {

            case PsyanimPlayfightBehavior.STATE.CHARGING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: CHARGING');
                }

                break;

            case PsyanimPlayfightBehavior.STATE.WANDERING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: WANDERING');
                }

                break;

            case PsyanimPlayfightBehavior.STATE.FLEEING:

                if (this.debug)
                {
                    PsyanimDebug.log(this.entity.name + ' entered playfight state: FLEEING');
                }

                break;
        }
    }

    updateBreakTimer(dt) {

        if (this._state == PsyanimPlayfightBehavior.STATE.WANDERING)
        {
            this._breakTimer += dt;

            if (this._breakTimer >= this.breakDuration)
            {
                this._setState(PsyanimPlayfightBehavior.STATE.CHARGING);
            }
        }
        else if (this._state == PsyanimPlayfightBehavior.STATE.FLEEING)
        {
            this._breakTimer += dt;
        }
    }

    getSteering(target) {

        let distanceToTarget = 0;

        switch (this._state) {

            case PsyanimPlayfightBehavior.STATE.CHARGING:

                return this.arriveBehavior.getSteering(target);

            case PsyanimPlayfightBehavior.STATE.WANDERING:

                distanceToTarget = this.entity.position
                    .subtract(target.position)
                    .length();

                if (distanceToTarget < this.fleeBehavior.panicDistance)
                {
                    this._setState(PsyanimPlayfightBehavior.STATE.FLEEING);

                    return this.fleeBehavior.getSteering(target);
                }

                return this.wanderBehavior.getSteering();

            case PsyanimPlayfightBehavior.STATE.FLEEING:

                distanceToTarget = this.entity.position
                    .subtract(target.position)
                    .length();

                if (distanceToTarget > this.fleeBehavior.panicDistance)
                {
                    this._setState(PsyanimPlayfightBehavior.STATE.WANDERING);

                    return this.wanderBehavior.getSteering();
                }

                return this.fleeBehavior.getSteering(target);
        }
    }
}

