import PsyanimComponent from '../../PsyanimComponent.js';

import {
    PsyanimAnimationClip
} from 'psyanim-utils';

export default class PsyanimAnimationBaker extends PsyanimComponent {

    constructor(entity) {

        super(entity);

        this._clip = new PsyanimAnimationClip();
        this._state = PsyanimAnimationBaker.STATE.IDLE;

        this._animationParameters = [];
    }

    afterCreate() {

        super.afterCreate();

        // gather component references for each parameter
        this._animationParameters.forEach(parameter => {

            let componentReference = this.entity.getComponent(parameter.componentInfo.type);

            // just store the component reference in the parameter object itself
            parameter.componentReference = componentReference;
        })
    }

    get isRunning() {

        return this._state == PsyanimAnimationBaker.STATE.BAKING;
    }

    addAnimationParameter(parameter) {

        this._animationParameters.push(parameter);
    }

    addAnimationParameters(parameters) {

        this._animationParameters.push(...parameters);
    }

    start() {

        this._state = PsyanimAnimationBaker.STATE.BAKING;
    }

    stop() {

        this._state = PsyanimAnimationBaker.STATE.IDLE;
    }

    clear() {

        this._clip.clear();
    }

    update(t, dt) {

        super.update(t, dt);

        switch (this._state)
        {
            case PsyanimAnimationBaker.STATE.IDLE:

                break;

            case PsyanimAnimationBaker.STATE.BAKING:

                let optionalAttributes = {};

                // update animation parameter values
                this._animationParameters.forEach(parameter => {

                    let componentReference = parameter.componentReference;

                    optionalAttributes[parameter.name] = componentReference[parameter.key];
                });

                // save sample to animation clip
                this._clip.addSample(t, this.entity.position, this.entity.rotation, optionalAttributes);

                break;
        }
    }

    get clip() {

        return this._clip;
    }
}

PsyanimAnimationBaker.STATE = {

    IDLE: 0x0001,
    BAKING: 0x0002,
};

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimAnimationPlayer extends PsyanimComponent {

    constructor(entity) {

        super(entity);

        this._clip = null;
        this._currentIndex = 0;

        this.events = new Phaser.Events.EventEmitter();
    }

    play(animationClip) {

        this._clip = animationClip;
        this._currentIndex = 0;
        this._nSamples = animationClip.getSampleCount();

        this.entity.setPhysicsEnabled(false);
    }

    stop() {

        this._clip = null;
        this._currentIndex = 0;
        this._nSamples = 0;
    }

    destroy() {

        this.stop();

        super.destroy();
    }

    update(t, dt) {

        super.update(t, dt);

        if (this._clip != null)
        {
            if (this._currentIndex < this._nSamples)
            {
                let currentSample = this._clip.getSample(this._currentIndex);

                this.entity.position = new Phaser.Math.Vector2(
                    currentSample.x, currentSample.y);

                this.entity.rotation = currentSample.rotation;

                this._currentIndex++;
            }
            else
            {
                this._clip = null;

                this.entity.setPhysicsEnabled(true);

                this.events.emit('playbackComplete');
            }
        }
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimExperimentTimer extends PsyanimComponent {

    constructor(entity) {

        super(entity);

        this._callback = null;
        this._callbackArgs = null;
        this._callbackScope = null;

        this._currentTimer = null;
    }

    setOnTimerElapsed(callback, args = [], callbackScope = null) {

        this._callback = callback;
        this._callbackArgs = args;
        this._callbackScope = callbackScope;
    }

    start(duration) {

        if (this.isRunning)
        {
            this.stop();
        }

        this._currentTimer = this.entity.scene.time.delayedCall(
            duration, this._callback, this._args, this._callbackScope
        );
    }

    get isRunning() {

        return this._currentTimer != null;
    }

    stop() {

        if (this._currentTimer == null)
        {
            return;
        }

        this._currentTimer.paused = true;
        this._currentTimer.remove();
        this._currentTimer = null;
    }
}

import PsyanimComponent from '../../PsyanimComponent.js';

import PsyanimBasicHFSM from '../ai/PsyanimBasicHFSM.js';
import PsyanimFSM from '../ai/PsyanimFSM.js';

import { PsyanimDebug } from 'psyanim-utils';

export default class PsyanimFSMStateRecorder extends PsyanimComponent {

    stateMachine;

    saveResumeEventSnapshot;
    savePauseEventSnapshot;
    saveStopEventSnapshot;
    saveEnterEventSnapshot;
    saveExitEventSnapshot;

    recordOnStart;

    debug;

    constructor(entity) {

        super(entity);

        this._stateBuffer = [];

        this.recordOnStart = true;

        this.saveResumeEventSnapshot = false;
        this.savePauseEventSnapshot = false;
        this.saveStopEventSnapshot = false;
        this.saveEnterEventSnapshot = false;
        this.saveExitEventSnapshot = false;

        this.debug = false;

        this._fsmResumedHandler = this._handleFSMResumed.bind(this);
        this._fsmPausedHandler = this._handleFSMPaused.bind(this);
        this._fsmStoppedHandler = this._handleFSMStopped.bind(this);
        this._fsmEnterHandler = this._handleFSMStateEntered.bind(this);
        this._fsmExitHandler = this._handleFSMStateExited.bind(this);

        this._recording = false;

        this._keys = {
            Q: entity.scene.input.keyboard.addKey(Phaser.Input.Keyboard.KeyCodes.Q),
            R: entity.scene.input.keyboard.addKey(Phaser.Input.Keyboard.KeyCodes.R),
            SPACE: entity.scene.input.keyboard.addKey(Phaser.Input.Keyboard.KeyCodes.SPACE),
        };
    }

    get data() {

        return this._stateBuffer;
    }

    record() {

        if (this._recording)
        {
            return;
        }

        if (this.stateMachine instanceof PsyanimBasicHFSM)
        {
            this.stateMachine.events.on('resume', this._fsmResumedHandler);
            this.stateMachine.events.on('pause', this._fsmPausedHandler);
            this.stateMachine.events.on('stop', this._fsmStoppedHandler);
            this.stateMachine.events.on('enter', this._fsmEnterHandler);
            this.stateMachine.events.on('exit', this._fsmExitHandler);
        }
        else if (this.stateMachine instanceof PsyanimFSM)
        {
            this.stateMachine.events.on('enter', this._fsmEnterHandler);
            this.stateMachine.events.on('exit', this._fsmExitHandler);
        }
        else
        {
            PsyanimDebug.error("'stateMachine' must be an instance of PsyanimFSM or PsyanimBasicFSM!");
        }

        this._recording = true;
    }

    stop() {
        
        if (this.stateMachine instanceof PsyanimBasicHFSM)
        {
            this.stateMachine.events.off('resume', this._fsmResumedHandler);
            this.stateMachine.events.off('pause', this._fsmPausedHandler);
            this.stateMachine.events.off('stop', this._fsmStoppedHandler);
            this.stateMachine.events.off('enter', this._fsmEnterHandler);
            this.stateMachine.events.off('exit', this._fsmExitHandler);
        }
        else if (this.stateMachine instanceof PsyanimFSM)
        {
            this.stateMachine.events.off('enter', this._fsmEnterHandler);
            this.stateMachine.events.off('exit', this._fsmExitHandler);
        }
        else
        {
            PsyanimDebug.error("'stateMachine' must be an instance of PsyanimFSM or PsyanimBasicFSM!");
        }

        this._recording = false;
    }

    afterCreate() {

        super.afterCreate();

        if (this.recordOnStart)
        {
            this.record();
        }
    }

    _handleFSMStateEntered(stateName) {

        let data = {
            eventType: 'enter', 
            stateName: stateName,
            t: this.entity.scene.time.now - this.entity.scene.timeSinceLastInit
        };

        if (this.saveEnterEventSnapshot)
        {
            data.stateVariableSnapshot = this.stateMachine.getStateVariableSnapshot();
        }

        this._stateBuffer.push(data);
    }

    _handleFSMStateExited(stateName) {

        let data = {
            eventType: 'exit',
            stateName, stateName,
            t: this.entity.scene.time.now - this.entity.scene.timeSinceLastInit
        };

        if (this.saveExitEventSnapshot)
        {
            data.stateVariableSnapshot = this.stateMachine.getStateVariableSnapshot();
        }

        this._stateBuffer.push(data);
    }

    _handleFSMResumed(fsm) {

        let data = {
            eventType: 'resume',
            stateMachine: fsm.constructor.name,
            currentState: fsm.currentStateName,
            t: this.entity.scene.time.now - this.entity.scene.timeSinceLastInit
        };

        if (this.saveResumeEventSnapshot)
        {
            data.stateVariableSnapshot = this.stateMachine.getStateVariableSnapshot();
        }

        this._stateBuffer.push(data);
    }

    _handleFSMStopped(fsm) {

        let data = {
            eventType: 'stop',
            stateMachine: fsm.constructor.name,
            currentState: fsm.currentStateName,
            t: this.entity.scene.time.now - this.entity.scene.timeSinceLastInit
        };

        if (this.saveStopEventSnapshot)
        {
            data.stateVariableSnapshot = this.stateMachine.getStateVariableSnapshot();
        }

        this._stateBuffer.push(data);
    }

    _handleFSMPaused(fsm) {

        let data = {
            eventType: 'pause',
            stateMachine: fsm.constructor.name,
            currentState: fsm.currentStateName,
            t: this.entity.scene.time.now - this.entity.scene.timeSinceLastInit
        };

        if (this.savePauseEventSnapshot)
        {
            data.stateVariableSnapshot = this.stateMachine.getStateVariableSnapshot();
        }

        this._stateBuffer.push(data);
    }

    update(t, dt) {

        super.update(t, dt);

        if (this.debug)
        {
            if (Phaser.Input.Keyboard.JustDown(this._keys.SPACE))
            {
                let length = JSON.stringify(this._stateBuffer).length;

                console.log('PsyanimFSMStateRecorder buffer length =', length, ', buffer =', this._stateBuffer);
            }

            if (Phaser.Input.Keyboard.JustDown(this._keys.R))
            {
                this.record();
            }

            if (Phaser.Input.Keyboard.JustDown(this._keys.Q))
            {
                this.stop();
            }
        }
    }
}

import Phaser from 'phaser';

import PsyanimComponent from '../../PsyanimComponent.js';

export default class PsyanimVideoRecorder extends PsyanimComponent {

    constructor(entity) {

        super(entity);

        this.events = new Phaser.Events.EventEmitter();

        this._chunks = [];

        this._state = PsyanimVideoRecorder.STATE.READY_TO_RECORD;

        // setup media recorder
        this._mediaRecorder = new MediaRecorder(this.entity.scene.
            game.canvas.captureStream(60));

        this._mediaRecorder.ondataavailable = (e) => {
            this._chunks.push(e.data);
        };

        this._mediaRecorder.onstop = (e) => {

            this._state = PsyanimVideoRecorder.STATE.VIDEO_AVAILABLE;

            this.events.emit('videoFileReady');
        };
    }

    start() {

        this._state = PsyanimVideoRecorder.STATE.RECORDING;

        this._chunks = [];

        this._mediaRecorder.start();
    }

    stop() {

        this._mediaRecorder.stop();
    }

    getVideoBlob() {

        return new Blob(this._chunks, { type: "video/webm" });
    }

    update(t, dt) {

        super.update(t, dt);
    }
}

PsyanimVideoRecorder.STATE = {
