export default class PsyanimClickToMovePlayerPrefab extends PsyanimEntityPrefab {

    grid;

    maxSpeed;
    innerDecelerationRadius;
    outerDecelerationRadius;

    debug;

    constructor(shapeParams = { isEmpty: true }, matterOptions = {}) {

