import PsyanimScene from './PsyanimScene.js';

import PsyanimNavigationGrid from './utils/PsyanimNavigationGrid.js';
import PsyanimComponent from './PsyanimComponent.js';
import PsyanimEntityPrefab from './PsyanimEntityPrefab.js';

import {
    PsyanimDebug,
    PsyanimUtils
} from 'psyanim-utils';

/**
 *  Algorithm (order is important!):
 * 
 *      - Validate scene definition adheres to schema
 *      - Setup navgrid, if configured
 *      - Instantiate all non-prefab entities
 *      - Add all components to non-prefab entities
 *      - Configure all prefabs' value-type fields (non-entity or non-components fields)
 *      - Instantiate all prefab entities in the scene
 *      - Configure all entities' components
 * 
 */

export default class PsyanimDataDrivenScene extends PsyanimScene {

    debug;

    constructor(key = null) {

        let sceneKey = 'PsyanimDataDrivenScene';

        if (key)
        {
            sceneKey = key;
        }

        super(sceneKey);

        this.debug = false;
    }

    create() {

        super.create();

        this._sceneDefinition = this.registry.get('psyanim_currentSceneDefinition');

        if (Object.hasOwn(this._sceneDefinition, 'wrapScreenBoundary'))
        {
            this.screenBoundary.wrap = this._sceneDefinition.wrapScreenBoundary;
        }

        this._validateSceneDefinition();
        this._setupNavgrid();
        this._instantiateNonPrefabEntities();
        this._instantiatePrefabEntities();
        this._configureEntities();
    }

    _validateSceneDefinition() {

        if (this.debug)
        {
            PsyanimDebug.log('******** Validating Scene Definition ********');
        }

        // validate scene definition exists
        if (!this._sceneDefinition)
        {
            PsyanimDebug.error('No valid scene definition configured!');
        }

        // validate 'wrapScreenBoundary'
        if (Object.hasOwn(this._sceneDefinition, 'wrapScreenBoundary'))
        {
            if (typeof(this._sceneDefinition.wrapScreenBoundary) != 'boolean')
            {
                PsyanimDebug.error("'wrapScreenBoundary' must be a boolean!");
            }
        }

        // validate 'navgrid' field
        if (Object.hasOwn(this._sceneDefinition, 'navgrid'))
        {
            if (Object.hasOwn(this._sceneDefinition.navgrid, 'cellSize'))
            {
                if (typeof(this._sceneDefinition.navgrid.cellSize) !== 'number')
                {
                    PsyanimDebug.error("navgrid 'cellSize' must be a 'number'!");
                }
            }

            if (Object.hasOwn(this._sceneDefinition.navgrid, 'obstacles'))
            {
                if (!Array.isArray(this._sceneDefinition.navgrid.obstacles))
                {
                    PsyanimDebug.error("navgrid 'obstacles' field must be an array!");
                }

                let obstacleDefinitions = this._sceneDefinition.navgrid.obstacles;

                for (let i = 0; i < obstacleDefinitions.length; ++i)
                {
                    let obstacleDefinition = obstacleDefinitions[i];

                    if (!obstacleDefinition.position || !PsyanimUtils.isObject(obstacleDefinition.position) ||
                        !Object.hasOwn(obstacleDefinition.position, 'x') ||
                        !Object.hasOwn(obstacleDefinition.position, 'y'))
                    {
                        PsyanimDebug.error("Navgrid obstacle at index '" + i + "' must have a 'position' field of the form: " + 
                            "{ x: 0, y: 0 }");
                    }

                    if (Object.hasOwn(obstacleDefinition, 'shapeParams'))
                    {
                        if (!PsyanimUtils.isObject(obstacleDefinition.shapeParams))
                        {
                            PsyanimDebug.error("Navgrid obstacle at index '" + i + "' 'shapeParams' field must be an object!");
                        }
                    }
                }
            }
        }

        // validate entity configurations
        if (Object.hasOwn(this._sceneDefinition, 'entities'))
        {
            if (!Array.isArray(this._sceneDefinition.entities))
            {
                PsyanimDebug.error("'entities' must be an array!");
            }

            let entityDefinitions = this._sceneDefinition.entities;

            // check entity name exists and is unique
            let entityNames = entityDefinitions.map(e => e.name);

            if (entityNames.includes(undefined))
            {
                let index = entityNames.indexOf(undefined);

                PsyanimDebug.error("Entity at index '" + index + "' has no 'name' field!");
            }

            if (entityNames.length !== new Set(entityNames).size) // uniqueness check
            {
                PsyanimDebug.error("All entity names must be unique!");
            }

            // now do the detailed checks on each entity
            for (let i = 0; i < entityDefinitions.length; ++i)
            {
                let entityDefinition = entityDefinitions[i];

                if (typeof(entityDefinition.name) != 'string')
                {
                    PsyanimDebug.error("Entity index '" + i + "': 'name' must be of type 'string'!");
                }

                if (Object.hasOwn(entityDefinition, 'instances'))
                {
                    if (typeof(entityDefinition.instances) !== 'number')
                    {
                        PsyanimDebug.error("Entity at index = " + i + ": 'instances' field must be a number!");
                    }
                }

                if (Object.hasOwn(entityDefinition, 'initialPosition'))
                {
                    if (!PsyanimUtils.isObject(entityDefinition.initialPosition) && entityDefinition.initialPosition !== 'random')
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has an invalid 'initialPosition'!");
                    }

                    if (entityDefinition.initialPosition !== 'random')
                    {
                        if (!Object.hasOwn(entityDefinition.initialPosition, 'x'))
                        {
                            PsyanimDebug.error("Entity index '" + i + "' has an invalid 'initialPosition' - no 'x' value!");
                        }
    
                        if (!Object.hasOwn(entityDefinition.initialPosition, 'y'))
                        {
                            PsyanimDebug.error("Entity index '" + i + "' has an invalid 'initialPosition' - no 'y' value!");
                        }    
                    }
                }

                if (Object.hasOwn(entityDefinition, 'initialAngle'))
                {
                    if (typeof(entityDefinition.initialAngle) != 'number' && entityDefinition.initialAngle !== 'random')
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has an invalid 'initialAngle'!");
                    }
                }

                if (Object.hasOwn(entityDefinition, 'shapeParams'))
                {
                    if (!PsyanimUtils.isObject(entityDefinition.shapeParams))
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has invalid 'shapeParams' parameter - shapeParams must be an object!");   
                    }
                }

                // validate entity prefabs
                if (Object.hasOwn(entityDefinition, 'prefab'))
                {
                    if (!PsyanimUtils.isObject(entityDefinition.prefab))
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has invalid 'prefab' parameter - must be an object!");
                    }

                    if (!Object.hasOwn(entityDefinition.prefab, 'type'))
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has a 'prefab' parameter that's missing a 'type' field!");
                    }

                    if (!(entityDefinition.prefab.type.prototype instanceof PsyanimEntityPrefab))
                    {
                        PsyanimDebug.error("Entity index '" + i + "' has a 'prefab' type that doesn't inherit from PsyanimEntityPrefab!");
                    }

                    if (Object.hasOwn(entityDefinition.prefab, 'params'))
                    {
                        if (!PsyanimUtils.isObject(entityDefinition.prefab.params))
                        {
                            PsyanimDebug.error("Entity index '" + i + "' has an invalid prefab 'params' field.");
                        }

                        let paramKeys = Object.keys(entityDefinition.prefab.params);

                        for (let j = 0; j < paramKeys.length; ++j)
                        {
                            let paramKey = paramKeys[j];
                            let paramValue = entityDefinition.prefab.params[paramKey];
    
                            if (PsyanimUtils.isObject(paramValue) && Object.hasOwn(paramValue, 'entityName'))
                            {
                                PsyanimDebug.error("Entity index '" + i + "', prefab param index = '" + j + 
                                    "': prefab parameters shouldn't reference entities!");

                                if (Object.hasOwn(paramValue, 'componentType') || 
                                    Object.hasOwn(paramValue, 'componentId'))
                                {
                                    PsyanimDebug.error("Entity index '" + i + "', param key '" + paramKey + 
                                        "': prefab parameters shouldn't reference other components!");
                                }
                            }
                        }                        
                    }
                }

                // validate components on entities
                if (Object.hasOwn(entityDefinition, 'components'))
                {
                    let componentDefinitions = entityDefinition.components;

                    if (!Array.isArray(componentDefinitions))
                    {
                        PsyanimDebug.error("Entity index '" + i +"': 'components' field must be an array!");
                    }

                    for (let j = 0; j < componentDefinitions.length; ++j)
                    {
                        let componentDefinition = componentDefinitions[j];

                        if (!PsyanimUtils.isObject(componentDefinition) || !Object.hasOwn(componentDefinition, 'type'))
                        {
                            PsyanimDebug.error("Entity index '" + i + "', component index '" + j + 
                                "': Component definition must be an object with a 'type' field!");
                        }

                        if (!(componentDefinition.type.prototype instanceof PsyanimComponent))
                        {
                            PsyanimDebug.error("Entity index '" + i + "', component index '" + j + 
                                "': Component definition 'type' must be a type that inherits from PsyanimComponent!");
                        }

                        if (Object.hasOwn(componentDefinition, 'params'))
                        {
                            if (!PsyanimUtils.isObject(componentDefinition.params))
                            {
                                PsyanimDebug.error("Entity index '" + i + "', component index '" + j + 
                                "': Component 'params' must be an object!");
                            }

                            let paramKeys = Object.keys(componentDefinition.params);

                            for (let k = 0; k < paramKeys.length; ++k)
                            {
                                let paramKey = paramKeys[k];
                                let paramValue = componentDefinition.params[paramKey];
    
                                if (PsyanimUtils.isObject(paramValue))
                                {
                                    if (Object.hasOwn(paramValue, 'entityName'))
                                    {
                                        if (!entityNames.includes(paramValue.entityName))
                                        {
                                            PsyanimDebug.error("Entity index '" + i + "', component index '" + j + 
                                                "', param key '" + paramKey + "': entity name doesn't exist in scene definition!");
                                        }
        
                                        if (Object.hasOwn(paramValue, 'componentType') || 
                                            Object.hasOwn(paramValue, 'componentId'))
                                        {
                                            let componentType = paramValue.componentType;
                                            let componentId = paramValue.componentId;

                                            let entityDefinition = entityDefinitions.find(e => e.name == paramValue.entityName);
        
                                            if (!Object.hasOwn(entityDefinition, 'components'))
                                            {
                                                PsyanimDebug.warn("Entity index '" + i + "', component index '" + j + 
                                                "', param key '" + paramKey + "': entity doesn't have any configured components!");
                                            }
        
                                            if (componentId)
                                            {
                                                if (typeof componentId !== 'number')
                                                {
                                                    PsyanimDebug.error("Entity index '" + i + "', component id '" + componentId + 
                                                        " must be an integer type!");
                                                }
                                            }
                                            else if (componentType)
                                            {
                                                let componentDefinition = entityDefinition.components
                                                    .find(c => c.type == componentType);

                                                if (!componentDefinition)
                                                {
                                                    PsyanimDebug.error("Entity index '" + i + "', component type '" + typeof(componentDefinition) + 
                                                    "', param key '" + paramKey + "': failed to find component on entity!");
                                                }
                                            }
                                        }
                                    }
                                }
                            }                            
                        }
                    }
                }
            }
        }
        else
        {
            PsyanimDebug.warn('No entities added to the scene definition!');
        }
    }

    _getRandomizedInitialPosition() {

        let canvasWidth = this.game.config.width;
        let canvasHeight = this.game.config.height;

        let canvasCenter = {
            x: canvasWidth / 2,
            y: canvasHeight / 2
        };

        let maxDeltaX = (canvasWidth - 100) / 2;
        let maxDeltaY = (canvasHeight - 100) / 2;

        let deltaX = (Math.random() * 2 - 1) * maxDeltaX;
        let deltaY = (Math.random() * 2 - 1) * maxDeltaY;

        return {
            x: canvasCenter.x + deltaX,
            y: canvasCenter.y + deltaY
        };
    }

    _configureEntityComponents(entityDefinition, nameOverride = null) {

        let name = (nameOverride) ? nameOverride : entityDefinition.name;

        let entityReference = this.getEntityByName(name);

        let componentDefinitions = entityDefinition.components;

        if (componentDefinitions)
        {
            for (let j = 0; j < componentDefinitions.length; ++j)
            {
                let componentDefinition = componentDefinitions[j];

                let componentReference = null;

                if (Object.hasOwn(componentDefinition, 'id'))
                {
                    componentReference = entityReference.getComponentByID(componentDefinition.id);
                }
                else
                {
                    componentReference = entityReference.getComponent(componentDefinition.type);
                }

                if (Object.hasOwn(componentDefinition, 'enabled'))
                {
                    componentReference.enabled = componentDefinition.enabled;
                }

                let componentParams = componentDefinition.params;

                if (componentParams)
                {
                    let paramNames = Object.keys(componentParams);

                    for (let k = 0; k < paramNames.length; ++k)
                    {
                        let paramName = paramNames[k];

                        let paramValue = componentParams[paramName];

                        if (PsyanimUtils.isObject(paramValue) && Object.hasOwn(paramValue, 'entityName'))
                        {
                            // param value an entity or component reference
                            let targetEntityName = paramValue.entityName;
                            
                            let targetEntity = this.getEntityByName(targetEntityName);

                            if (Object.hasOwn(paramValue, 'componentType'))
                            {
                                // target is a component reference, by 'type'
                                let componentType = paramValue.componentType;
                                let targetComponent = targetEntity.getComponent(componentType);

                                componentReference[paramName] = targetComponent;
                            }
                            else if (Object.hasOwn(paramValue, 'componentId'))
                            {
                                // target is a component reference, by 'id'
                                let componentId = paramValue.componentId;
                                let targetComponent = targetEntity.getComponentByID(componentId);

                                componentReference[paramName] = targetComponent;
                            }
                            else
                            {
                                // target is an entity reference
                                componentReference[paramName] = targetEntity;
                            }
                        }
                        else
                        {
                            componentReference[paramName] = paramValue;
                        }
                    }
                }
            }    
        }
    }

    _configureEntities() {

        let entityDefinitions = this._sceneDefinition.entities;

        if (!entityDefinitions || entityDefinitions.length == 0)
        {
            return;
        }

        /** finally, configure all the components */
        for (let i = 0; i < entityDefinitions.length; ++i)
        {
            let entityDefinition = entityDefinitions[i];

            let nInstances = Object.hasOwn(entityDefinition, 'instances') ? 
                entityDefinition.instances : 1;

            if (nInstances < 1)
            {
                PsyanimDebug.error("'instances' can not be less than 1!  entity name: " + entityDefinition.name);
            }

            if (nInstances == 1)
            {
                this._configureEntityComponents(entityDefinition);
            }
            else
            {
                for (let j = 0; j < nInstances; ++j)
                {
                    let instanceName = entityDefinition.name + '_' + (j + 1);

                    this._configureEntityComponents(entityDefinition, instanceName);
                }    
            }
        }
    }

    _instantiatePrefabEntity(entityDefinition, nameOverride = null) {

        let name = (nameOverride) ? nameOverride : entityDefinition.name;

        // compute initial position
        let initialPosition = null;
        let angle = 0;

        if (Object.hasOwn(entityDefinition, 'initialPosition'))
        {
            if (entityDefinition.initialPosition === 'random')
            {
                initialPosition = this._getRandomizedInitialPosition();
            }
            else
            {
                initialPosition = entityDefinition.initialPosition;
            }
        }
        else
        {
            initialPosition = { x: 0, y: 0 };
        }

        if (Object.hasOwn(entityDefinition, 'initialAngle'))
        {
            if (entityDefinition.initialAngle === 'random')
            {
                angle = Math.random() * 360.0;
            }
            else
            {
                angle = entityDefinition.initialAngle;
            }
        }

        let shapeParams = Object.hasOwn(entityDefinition, 'shapeParams') ? 
            entityDefinition.shapeParams : { isEmpty: true};

        let matterOptions = Object.hasOwn(entityDefinition, 'matterOptions') ?
            entityDefinition.matterOptions : {};

        let prefab = new entityDefinition.prefab.type(shapeParams, matterOptions);

        /** configure the prefab first */
        if (Object.hasOwn(entityDefinition.prefab, 'params'))
        {
            let prefabParamNames = Object.keys(entityDefinition.prefab.params);

            for (let j = 0; j < prefabParamNames.length; ++j)
            {
                let paramName = prefabParamNames[j];
    
                let paramValue = entityDefinition.prefab.params[paramName];
    
                if (paramValue === 'navgrid')
                {
                    prefab[paramName] = this._grid;
                }
                else if (PsyanimUtils.isObject(paramValue) && Object.hasOwn(paramValue, 'entityName'))
                {
                    PsyanimDebug.error('Prefab parameters should not reference other entities or components!');
                }
                else
                {
                    prefab[paramName] = paramValue;
                }
            }
        }

        /** instantiate the prefab after it's been properly configured */
        let newEntity = this.instantiatePrefab(prefab, name,
            initialPosition.x, initialPosition.y);

        newEntity.angle = angle;
    }

    _instantiatePrefabEntities() {

        let entityDefinitions = this._sceneDefinition.entities;

        if (!entityDefinitions || entityDefinitions.length == 0)
        {
            return;
        }

        let prefabEntityDefinitions = entityDefinitions
            .filter(e => Object.hasOwn(e, 'prefab'));

        for (let i = 0; i < prefabEntityDefinitions.length; ++i)
        {
            let entityDefinition = prefabEntityDefinitions[i];

            let nInstances = Object.hasOwn(entityDefinition, 'instances') ? 
                entityDefinition.instances : 1;

            if (nInstances < 1)
            {
                PsyanimDebug.error("'instances' can not be less than 1!  entity name: " + entityDefinition.name);
            }

            if (nInstances == 1)
            {
                this._instantiatePrefabEntity(entityDefinition);
            }
            else
            {
                for (let j = 0; j < nInstances; ++j)
                {
                    let instanceName = entityDefinition.name + '_' + (j + 1);

                    this._instantiatePrefabEntity(entityDefinition, instanceName);
                }    
            }
        }
    }

    _instantiateNonPrefabEntity(entityDefinition, nameOverride = null) {

        if (this.debug)
        {
            PsyanimDebug.log("****** Instantiating a Non-Prefab Entity ******");
        }

        /** Instantiate the non-prefab entity first */
        let name = (nameOverride) ? nameOverride : entityDefinition.name;

        let initialPosition = null;
        let angle = 0;

        if (Object.hasOwn(entityDefinition, 'initialPosition'))
        {
            if (entityDefinition.initialPosition === 'random')
            {
                initialPosition = this._getRandomizedInitialPosition();
            }
            else
            {
                initialPosition = entityDefinition.initialPosition;
            }
        }
        else
        {
            initialPosition = { x: 0, y: 0 };
        }

        if (Object.hasOwn(entityDefinition, 'initialAngle'))
        {
            if (entityDefinition.initialAngle === 'random')
            {
                angle = Math.random() * 360.0;
            }
            else
            {
                angle = entityDefinition.initialAngle;
            }
        }

        let shapeParams = Object.hasOwn(entityDefinition, 'shapeParams') ? 
            entityDefinition.shapeParams : { isEmpty: true };

        let matterOptions = Object.hasOwn(entityDefinition, 'matterOptions') ?
            entityDefinition.matterOptions : {};

        let newEntity = this.addEntity(name, initialPosition.x, initialPosition.y, 
            shapeParams, matterOptions);

        newEntity.angle = angle;

        /** Then add all the components */
        let componentDefinitions = entityDefinition.components;

        if (componentDefinitions)
        {
            for (let j = 0; j < componentDefinitions.length; ++j)
            {
                let componentDefinition = componentDefinitions[j];

                let componentReference = newEntity.addComponent(componentDefinition.type);

                if (componentDefinition.id)
                {
                    // if component definition has an ID specified, we'll override the autogenerated one
                    componentReference.id = componentDefinition.id;
                }
                else
                {
                    // set componentDefinition 'id' to find the component reference later
                    componentDefinition.id = componentReference.id;
                }
            }    
        }
    }

    _instantiateNonPrefabEntities() {

        if (this.debug)
        {
            PsyanimDebug.log('******** Instantiating Non-Prefab Entities ********');
        }

        let entityDefinitions = this._sceneDefinition.entities;

        if (!entityDefinitions || entityDefinitions.length == 0)
        {
            return;
        }

        let nonPrefabEntityDefinitions = entityDefinitions
            .filter(e => !Object.hasOwn(e, 'prefab'));

        for (let i = 0; i < nonPrefabEntityDefinitions.length; ++i)
        {
            let entityDefinition = nonPrefabEntityDefinitions[i];

            if (!Object.hasOwn(entityDefinition, 'name'))
            {
                PsyanimDebug.error("No 'name' field found in entity definition!");
                return;
            }

            let nInstances = Object.hasOwn(entityDefinition, 'instances') ? 
                entityDefinition.instances : 1;

            if (nInstances < 1)
            {
                PsyanimDebug.error("'instances' can not be less than 1!  entity name: " + entityDefinition.name);
            }

            if (nInstances == 1)
            {
                this._instantiateNonPrefabEntity(entityDefinition);
            }
            else
            {
                for (let j = 0; j < nInstances; ++j)
                {
                    let instanceName = entityDefinition.name + '_' + (j + 1);

                    this._instantiateNonPrefabEntity(entityDefinition, instanceName);
                }    
            }
        }
    }

    _setupNavgrid() {

        let gridDefinition = this._sceneDefinition.navgrid;

        if (!gridDefinition)
        {
            return;
        }

        let cellSize = gridDefinition.cellSize;
        let obstacleDefinitions = gridDefinition.obstacles;

        this._grid = new PsyanimNavigationGrid(cellSize, 
            this.game.scale.width,
            this.game.scale.height);

        for (let i = 0; i < obstacleDefinitions.length; ++i)
        {
            let obstacleDefinition = obstacleDefinitions[i];

            let obstacle = this.addEntity('obstacle_' + i, 
                obstacleDefinition.position.x, 
                obstacleDefinition.position.y, 
                obstacleDefinition.shapeParams, 
                { isSleeping: true }
            );

            this._grid.addObstacle(obstacle);
        }

        this._grid.bake();
    }
}

PsyanimDataDrivenScene.KEY = 'PsyanimDataDrivenScene';

import Phaser from 'phaser';

import PsyanimConstants from './PsyanimConstants.js';
import PsyanimGeomUtils from './utils/PsyanimGeomUtils.js';

import PsyanimApp from './PsyanimApp.js';

import PsyanimSensor from './components/physics/PsyanimSensor.js';

import {
    PsyanimDebug
} from 'psyanim-utils';

/**
 *  Any object that exists in a scene, regardless of its visual representation, is called a `PsyanimEntity`.
 *
 *  More technically, it's an abstraction for anything that exists in a `Psyanim Scene` with a particular location, rotation, and (optionally) a visual representation which can have physics applied to it.
 *
 *  Entities may or may not have a visual representation in the scene.
 *
 *  Moreover, entities alone do not have any logic or behaviors. While entities have no user-defined state, they do have a position, orientation and velocity in the world, and can have forces / accelerations applied to them.
 *
 *  Any object in your simulation that needs a position or orientation, a visual representation in the scene, or needs to have physics applied to it, should be added to the scene as an entity.
 * 
 *  A `PsyanimEntity` acts a container for `PsyanimComponents`.
 * 
 *  All user-defined state and behaviors are encapsulated in PsyanimComponents, which are reusable scripts that can be attached to a PsyanimEntity and offer hooks into the real-time update loop of each scene.
 *
 *  -----------------------------------------------------------------------------------------------------------------
 * 
 *  Some helpful tips:
 *      - use 'this.visible' to toggle sprite visibility
 *      - use 'this.body.isSleeping' to toggle whether or not this object receives physics updates
 *      - use 'this.body.isSensor' to toggle whether this body collides with other bodies or not
 * 
 *  From: https://github.com/liabru/matter-js/issues/179
 * 
 *  "Internally the engine uses MKS (meters, kilograms, and seconds) units 
 *  and radians for angles.
 * 
 *  If you use the built in renderer and and built in runner, with default 
 *  settings this translates to:
 *  
 *      1 position = 1 px
 *      1 speed = 1 px per step
 *      1 step = 16.666ms"
 */
export default class PsyanimEntity extends Phaser.Physics.Matter.Sprite {

    /**
     * Constructor for PsyanimEntity objects.
     * 
     * To create entities in your app, use the PsyanimScene::addEntity() method.
     * 
     * This is usually only called by the PsyanimScene.
     * 
     * @param {PsyanimScene} scene - scene this entity belongs to.
     * @param {string} name - entity name, must be unique per scene!
     * @param {Number} [x] - x-coordinate of entity's initial position in the world
     * @param {Number} [y] - y-coordinate of entity's initial position in the world
     * @param {Object} [shapeParams] - object defines entity shape & color
     * @param {Object} [matterOptions] - object defines physics properties of entity for matter-js
     */
    constructor(scene, name, x = 0, y = 0, shapeParams = { isEmpty: true }, matterOptions = {}) {

        // before we call super(), let's generate the necessary textures and matter config
        let textureKey = null;

        if (shapeParams.textureKey) 
        {
            textureKey = shapeParams.textureKey;
        }
        else
        {
            textureKey = PsyanimApp.Instance.currentSceneKey + "_" + name;
        }

        const defaultShapeParams = PsyanimConstants.DEFAULT_ENTITY_SHAPE_PARAMS;

        let shapeType = Object.hasOwn(shapeParams, 'shapeType') ? shapeParams.shapeType : defaultShapeParams.shapeType;
        let color = Object.hasOwn(shapeParams, 'color') ? shapeParams.color : defaultShapeParams.color;

        let geomParams = null;
        let matterConfig = { type: 'circle', radius: 1 };

        let isEmpty = Object.hasOwn(shapeParams, 'isEmpty') ? shapeParams.isEmpty : false;

        if (isEmpty)
        {
            textureKey = '';
        }
        else
        {
            let generateNewTexture = false;

            if ( !scene.textures.exists(textureKey) )
            {
                generateNewTexture = true;
            }    

            switch(shapeType)
            {
                case PsyanimConstants.SHAPE_TYPE.CIRCLE:

                    let radius = (shapeParams.radius) ? shapeParams.radius : defaultShapeParams.radius;

                    let circleGeomParams = {
                        radius: radius
                    };

                    geomParams = circleGeomParams;

                    if (generateNewTexture)
                    {
                        PsyanimGeomUtils.generateCircleTexture(scene, textureKey, circleGeomParams, color);
                    }

                    matterConfig.type = 'circle';
                    matterConfig.radius = circleGeomParams.radius;

                    break;

                case PsyanimConstants.SHAPE_TYPE.TRIANGLE:

                    let base = (shapeParams.base) ? shapeParams.base : defaultShapeParams.base;
                    let altitude = (shapeParams.altitude) ? shapeParams.altitude : defaultShapeParams.altitude;
        
                    let triangleGeomParams = {
                        base: base, altitude: altitude
                    };

                    geomParams = triangleGeomParams;

                    if (generateNewTexture)
                    {
                        PsyanimGeomUtils.generateTriangleTexture(scene, textureKey, triangleGeomParams, color);
                    }

                    matterConfig.type = 'fromVertices';
                    matterConfig.verts = PsyanimGeomUtils.computeTriangleVertices(base, altitude);
        
                    break;

                case PsyanimConstants.SHAPE_TYPE.RECTANGLE:

                    let width = (shapeParams.width) ? shapeParams.width : defaultShapeParams.width;
                    let height = (shapeParams.height) ? shapeParams.height : defaultShapeParams.height;

                    let rectangleGeomParams = {
                        width: width, height: height
                    };

                    geomParams = rectangleGeomParams;

                    if (generateNewTexture)
                    {
                        PsyanimGeomUtils.generateRectangleTexture(scene, textureKey, rectangleGeomParams, color);
                    }

                    matterConfig.type = 'rectangle';
                    matterConfig.width = width;
                    matterConfig.height = height;

                    break;
            }
        }

        // initialize Phaser.Physics.Matter.Sprite
        super(scene.matter.world, x, y, textureKey);

        // save off this entity's properties
        this._shapeParams = shapeParams;
        this._matterOptions = matterOptions;
        this._initialPosition = { x: x, y: y };

        this._color = color;
        this._geomParams = geomParams;
        this._shapeType = shapeType;
        
        this.name = name;

        // setup new physics body
        matterOptions.name = Object.hasOwn(matterOptions, 'name') ? matterOptions.name : name;
        matterOptions.isSensor = Object.hasOwn(matterOptions, 'isSensor') ? matterOptions.isSensor : false;
        matterOptions.isSleeping = Object.hasOwn(matterOptions, 'isSleeping') ? matterOptions.isSleeping : false;
        matterOptions.collisionFilter = Object.hasOwn(matterOptions, 'collisionFilter') 
            ? matterOptions.collisionFilter : PsyanimConstants.DEFAULT_SPRITE_COLLISION_FILTER;
        
        this.setBody(matterConfig, matterOptions);

        // there's a bug in phaser where 'label' doesn't get set for non-primitive bodies
        this.body.label = this.name;

        // setup visibility and isSensor / isSleeping depending on whether this object is empty
        this.body.isSleeping = matterOptions.isSleeping;

        if (isEmpty)
        {
            this.visible = false;

            this.body.isSensor = true;
        }
        else
        {
            this.visible = Object.hasOwn(shapeParams, 'visible') ? shapeParams.visible : defaultShapeParams.visible;

            this.body.isSensor = matterOptions.isSensor;
        }

        if (Object.hasOwn(shapeParams, 'depth'))
        {
            this.depth = shapeParams.depth;
        }

        let mass = 100;

        this.body.mass = mass;
        this.body.inverseMass = 1/mass;

        this.body.inertia = Infinity;
        this.body.inverseInertia = 0;

        // we add the entity to the scene in constructor to guarantee all entities always live in a scene
        scene.add.existing(this);

        // setup private fields
        this._components = [];
    }

    /**
     *  Parameters defining the shape of the entity.
     * 
     *  @return {Object}
     *  @property {boolean} isEmpty - If `true`, this entity will not have a visual representation or physics applied.
     *  @property {PsyanimConstants.SHAPE_TYPE} shapeType - can be a circle, rectangle, or triangle
     *  @property {number} radius - If `shapeType` is `PsyanimConstants.SHAPE_TYPE.CIRCLE`, `radius` parameter controls its size.
     *  @property {number} width - If `shapeType` is `PsyanimConstants.SHAPE_TYPE.RECTANGLE`, `width` and `height` parameters control its size.
     *  @property {number} height - If `shapeType` is `PsyanimConstants.SHAPE_TYPE.RECTANGLE`, `width` and `height` parameters control its size.
     *  @property {number} base - If `shapeType` is `PsyanimConstants.SHAPE_TYPE.TRIANGLE`, `base` and `altitude` parameters control its size.
     *  @property {number} altitude - If `shapeType` is `PsyanimConstants.SHAPE_TYPE.TRIANGLE`, `base` and `altitude` parameters control its size.
     *  @property {number} color - integer representing RGB color
     *  @property {boolean} visible - `true` if entity is visible
     */
    get shapeParams() {

        return this._shapeParams;
    }

    /**
     *  Parameters defining the physics settings of the entity.
     * 
     *  @return {Object}
     *  @property {boolean} isSensor - If true, entity will be able to emit collision events, but will not be receive physics updates
     *  @property {boolean} isSleeping - If true, entity will not have physics updates or receive collision events
     *  @property {Object} collisionFilter - matter-js collision filter
     */
    get matterOptions() {

        return this._matterOptions;
    }

    /**
     *  Entity shape type (defined in PsyanimConstants)
     * 
     *  @return {string}
     */
    get shapeType() {
        return this._shapeType;
    }

    /**
     *  Entity color
     * 
     *  @return {number}
     */
    get color() {
        return this._color;
    }

    /**
     *  Set's entity's color
     * 
     *  @param {number} - RGB color value
     */
    set color(value) {

        this.setTintFill(value);
    }

    /**
     *  Entity's initial position, supplied on construction
     * 
     *  @return {Object}
     *  @property {number} - x
     *  @property {number} - y
     */
    get initialPosition() {

        return this._initialPosition;
    }

    /**
     * Enabled friction on entity in physics simulation
     * 
     * @param {number} friction - 
     * @param {number} frictionAir - 
     * @param {number} frictionStatic - 
     */
    enableFriction(friction = 0.1, frictionAir = 0.01, frictionStatic = 0.5) {

        this.body.friction = friction;
        this.body.frictionAir = frictionAir;
        this.body.frictionStatic = frictionStatic;
    }

    /**
     * Disables friction on entity in physics simulation
     */
    disableFriction() {

        this.body.friction = 0;
        this.body.frictionAir = 0;
        this.body.frictionStatic = 0;
    }

    /**
     * Attaches a newly created instance of `componentType` to this entity.
     * 
     * @param {Type} componentType - the type of the component that should be added to this entity
     * @returns {PsyanimComponent} - reference to newly added PsyanimComponent
     */
    addComponent(componentType) {

        let newComponent = new componentType(this);

        newComponent.id = this.scene.getNewComponentID();

        this._components.push(newComponent);

        return newComponent;
    }

    /**
     * @param {Type} componentType 
     * @returns {boolean} - `true` if this entity has at least 1 component attached of type `componentType`
     */
    hasComponent(componentType) {

        return this.getComponent(componentType) != null;
    }

    /**
     * Get an attached component instance on this entity by type.
     * 
     * @param {Type} componentType 
     * @returns {PsyanimComponent}
     */
    getComponent(componentType) {

        let components = this._components.filter(c => c instanceof componentType);

        if (components.length == 0)
        {
            return null;
        }

        return components[0];
    }

    getComponentByID(id) {

        return this._components.find(c => c.id === id);
    }

    /**
     * Get an attached component instance on this entity by index.
     * 
     * Component indices are based on the order they were added using `AddComponent`
     * 
     * @param {number} index 
     * @returns {PsyanimComponent} - reference to attached component instance at `index`
     */
    getComponentByIndex(index) {

        return this._components[index];
    }

    /**
     * Gets a shallow copy of PsyanimComponents attached to this entity
     * @returns {PsyanimComponent[]}
     */
    getComponents(componentType = null) {

        if (componentType)
        {
            let components = this._components.filter(c => c instanceof componentType);

            if (components.length === 0)
            {
                return null;
            }

            return components;
        }
        else
        {
            return this._components.slice();
        }
    }

    /**
     * Enables / disables the physics simulation for this entity
     * 
     * @param {boolean} enabled 
     */
    setPhysicsEnabled(enabled) {

        this.body.isSensor = !enabled;
        this.body.isSleeping = !enabled;
    }

    /**
     * 
     * @returns An array of distances for each screen boundary as [left, right, top, bottom]
     */
    computeDistancesToScreenBoundaries() {

        return [
            this.position.x,
            this.scene._canvasWidth - this.position.x,
            this.position.y,
            this.scene._canvasHeight - this.position.y
        ];
    }
    
    /**
     * Destroys this entity, cleaning up all of its resources.
     * 
     * NOTE: When overriding this in a derived class, the base class destroy() should be called last.
     */
    destroy() {

        this._components.forEach(c => {
            c.destroy();
        });
        
        this._components = [];

        super.destroy();
    }

    _removeComponent(component) {

        this.scene.freeComponentID(component.id);

        this._components = this._components.filter(c => c != component);
    }

    /**
     * This entity's position in the scene.
     * 
     * @returns {Phaser.Math.Vector2}
     * @property {number} x
     * @property {number} y
     */
    get position() {

        return new Phaser.Math.Vector2(this.x, this.y);
    }

    /**
     *  Sets this entity's position in the scene.
     * 
     *  @param {Object} - vector of the form: { x, y }
     */
    set position(value) {

        /**
         * The x-coordinate of this entity's position in the scene.
         * 
         * @type {number}
         */
        this.x = value.x;

        /**
         * The y-coordinate of this entity's position in the scene.
         * 
         * @type {number}
         */
        this.y = value.y;
    }

    /**
     * A unit vector oriented in the direction this entity is facing.
     * 
     * @return {Phaser.Math.Vector2}
     */
    get forward() {

        return new Phaser.Math.Vector2(1, 0).setAngle(this.angle * Math.PI / 180);
    }

    /**
     * A unit vector oriented to the right of this entity's forward direction.
     * 
     * @return {Phaser.Math.Vector2} 
     */
    get right() {

        return this.forward.rotate(90 * Math.PI / 180);
    }

    /**
     * The velocity vector of this entity.
     * 
     * @return {Phaser.Math.Vector2}
     */
    get velocity() {

        let currentVelocityXY = this.scene.matter.body.getVelocity(this.body);

        return new Phaser.Math.Vector2(currentVelocityXY.x, currentVelocityXY.y);
    }

    _handleSensorTriggerEnter(entity, sensor) {

        let entityAlreadyIncluded = this._intersectingEntities.has(entity.psyanimSensorId);

        if (entityAlreadyIncluded)
        {
            let sensorIDs = this._intersectingEntities.get(entity.psyanimSensorId);

            if (sensorIDs.includes(sensor.psyanimSensorId))
            {
                PsyanimDebug.error('sensorIDs already includes ID: ', sensor.psyanimSensorId);
                return;
            }

            sensorIDs.push(sensor.psyanimSensorId);
        }
        else
        {
            let sensorIDs = [ sensor.psyanimSensorId ];

            this._intersectingEntities.set(entity.psyanimSensorId, sensorIDs);

            this._components.forEach(c => {

                c.onSensorEnter(entity);
            });
        }
    }

    _handleSensorTriggerExit(entity, sensor) {

        let sensorIDs = this._intersectingEntities.get(entity.psyanimSensorId);

        if (!sensorIDs.includes(sensor.psyanimSensorId))
        {
            PsyanimDebug.error('sensor not in entity sensorIDs list!');
            return;
        }

        sensorIDs = sensorIDs.filter(sid => sid !== sensor.psyanimSensorId);

        if (sensorIDs.length > 0)
        {
            this._intersectingEntities.set(entity.psyanimSensorId, sensorIDs)
        }
        else
        {
            this._intersectingEntities.delete(entity.psyanimSensorId);

            this._components.forEach(c => {

                c.onSensorExit(entity);
            });
        }
    }

    afterCreate() {

        this._sensors = [];

        let attachedSensorComponents = this.getComponents(PsyanimSensor);

        if (attachedSensorComponents)
        {
            this._sensors = this._sensors.concat(attachedSensorComponents);
        }

        this._intersectingEntities = new Map();

        this._handleSensorTriggerEnterCallback = this._handleSensorTriggerEnter.bind(this);
        this._handleSensorTriggerExitCallback = this._handleSensorTriggerExit.bind(this);

        this._sensors.forEach(sensor => {

            sensor.events.on('triggerEnter', this._handleSensorTriggerEnterCallback);
            sensor.events.on('triggerExit', this._handleSensorTriggerExitCallback);
        });

        this._components.forEach(c => {

            c.afterCreate();
        });
    }

    beforeShutdown() {

        this._components.forEach(c => {

            c.beforeShutdown();
        });
    }

    update(t, dt) {

        // update all components
        this._components.forEach(c => {

            if (c.enabled)
            {
