import PsyanimConstants from "../PsyanimConstants.js";

export default class PsyanimGeomUtils {

    /**
     *  NOTE: when generating a texture from any kind of Phaser graphics object, it's critical to place it at the center of 
     *  your viewport if you want the associated texture to be centered and not clipped.
     */

    static computeTriangleVertices(base, altitude) {

        return [

            {x: -altitude * (1/3), y: -base / 2},
            {x: altitude * (2/3), y: 0 },
            {x: -altitude * (1/3), y: base / 2},
        ];
    }

    /**
     * 
     * @param {*} scene 
     * @param {*} textureKey 
     * @param {*} geomParams
     * @param {*} color 
     */
    static generateTriangleTexture(scene, textureKey, geomParams = { base: 12, altitude: 24}, color = 0x0000ff) {

        let verts = PsyanimGeomUtils.computeTriangleVertices(geomParams.base, geomParams.altitude);

        // some funky stuff happens, so I was able to reverse-engineer how to align this w/ matter-js collision
        // and it involves increasing the canvas size by twice a magic number and shifting the geo by half that
        let magicNumber = 1/6;

        let textureWidth = geomParams.altitude * (1 + 2 * magicNumber);
        let textureHeight = geomParams.base;

        let translatedVerts = [
            { x: verts[0].x + ((1/2) + magicNumber) * geomParams.altitude, y: verts[0].y + (1/2) * geomParams.base },
            { x: verts[1].x + ((1/2) + magicNumber) * geomParams.altitude, y: verts[1].y + (1/2) * geomParams.base },
            { x: verts[2].x + ((1/2) + magicNumber) * geomParams.altitude, y: verts[2].y + (1/2) * geomParams.base }
        ];

        let graphics = scene.add.graphics();
        graphics.fillStyle(color);

        graphics.fillTriangle(
            translatedVerts[0].x, translatedVerts[0].y,
            translatedVerts[1].x, translatedVerts[1].y,
            translatedVerts[2].x, translatedVerts[2].y);

        graphics.generateTexture(textureKey, textureWidth, textureHeight);
        graphics.destroy();
    }

    /**
     * 
     * @param {*} scene 
     * @param {*} textureKey 
     * @param {*} geomParams
     * @param {*} color 
     */
    static generateCircleTexture(scene, textureKey, geomParams = { radius: 12 }, color = 0xffff00) {

        let graphics = scene.add.graphics();
        graphics.fillStyle(color);

        graphics.fillCircle(geomParams.radius, geomParams.radius, geomParams.radius);

        let diameter = geomParams.radius * 2;

        graphics.generateTexture(textureKey, diameter, diameter);
        graphics.destroy();
    }

    static generateRectangleTexture(scene, textureKey, geomParams = { width: 60, height: 30 }, color = 0xffff00) {

        let graphics = scene.add.graphics();
        graphics.fillStyle(color);

        graphics.fillRect(
            0, 0, geomParams.width, geomParams.height
        );

        graphics.generateTexture(textureKey, geomParams.width, geomParams.height);
        graphics.destroy();
    }

    static createTriangleSprite(scene, textureKey, 
        geomParams = { x: 400, y: 300, base: 12, altitude: 24 },
        bodyOptions = { collisionFilter: PsyanimConstants.DEFAULT_SPRITE_COLLISION_FILTER }) {

        let verts = PsyanimGeomUtils.computeTriangleVertices(geomParams.base, geomParams.altitude);

        let sprite = scene.matter.add.sprite(geomParams.x, geomParams.y, textureKey, null);

        sprite.setBody({
            type: 'fromVertices',
            verts: verts
        }, bodyOptions);

        return sprite;
    }

    static createCircleSprite(scene, textureKey, 
        geomParams = { x: 400, y: 300, radius: 12 }, 
        bodyOptions = { collisionFilter: PsyanimConstants.DEFAULT_SPRITE_COLLISION_FILTER }) {

        let sprite = scene.matter.add.sprite(geomParams.x, geomParams.y, textureKey);

        sprite.setBody({ 
            type: 'circle', 
            radius: geomParams.radius 
        }, bodyOptions);

        return sprite;
    }
}

 import PsyanimPath from "./PsyanimPath.js";
 
 export default class PsyanimNavigationGrid {
    
    constructor(cellSize, canvasWidth, canvasHeight) {

        if (canvasWidth % cellSize != 0)
        {
            console.error("ERROR: canvas width must be an integer multiple of cell size!");
        }

        if (canvasHeight % cellSize != 0)
        {
            console.error("ERROR: canvas height must be an integer multiple of cell size!");
        }

        this._obstacles = [];

        this._cellSize = cellSize;

        this._canvasWidth = canvasWidth;
        this._canvasHeight = canvasHeight;

        this._grid = [];
        
        for (let i = 0; i < this.rows; ++i)
        {
            let row = [];

            for (let j = 0; j < this.columns; ++j)
            {
                row.push(0);
            }

            this._grid.push(row);
        }
    }

    get rows() {

        return Math.floor(this._canvasHeight / this._cellSize);
    }

    get columns() {

        return Math.floor(this._canvasWidth / this._cellSize);
    }

    get cellSize() {

        return this._cellSize;
    }

    get matrix() {

        return this._grid;
    }

    addObstacle(entity) {

        this._obstacles.push(entity);
    }

    bake() {

        for (let i = 0; i < this._obstacles.length; ++i)
        {
            this._createObstacleFromEntityBounds(this._obstacles[i]);
        }
    }

    isWorldPointInWalkableRegion(point) {

        let gridPoint = this.convertToGridFromWorldCoords(point);

        // check if we're inside the canvas / grid
        if (gridPoint.x < 0 || gridPoint.x >= this.columns)
        {
            return false;
        }
        else if (gridPoint.y < 0 || gridPoint.y >= this.rows)
        {
            return false;
        }

        // check if the grid point is valid
        if (this._grid[gridPoint.y][gridPoint.x] == 0)
        {
            return true;
        }

        return false;
    }

    convertToGridFromWorldCoords(point) {

        let x = Math.floor(point.x / this._cellSize);
        let y = Math.floor(point.y / this._cellSize);
        
        return { x: x, y: y };
    }

    findClosestWalkableWorldPoint(point) {

        let upPoint = point.clone();
        let downPoint = point.clone();
        let leftPoint = point.clone();
        let rightPoint = point.clone();

        while (true)
        {
            upPoint.add(new Phaser.Math.Vector2(0, this.cellSize));
            downPoint.subtract(new Phaser.Math.Vector2(0, this.cellSize));

            rightPoint.add(new Phaser.Math.Vector2(this.cellSize, 0));
            leftPoint.subtract(new Phaser.Math.Vector2(this.cellSize, 0));

            if (this.isWorldPointInWalkableRegion(upPoint))
            {
                return upPoint;
            }

            if (this.isWorldPointInWalkableRegion(downPoint))
            {
                return downPoint;
            }

            if (this.isWorldPointInWalkableRegion(rightPoint))
            {
                return rightPoint;
            }

            if  (this.isWorldPointInWalkableRegion(leftPoint))
            {
                return leftPoint;
            }
        }
    }

    convertToWorldCoordsFromGrid(point) {

        let x = (point.x + 0.5) * this._cellSize;
        let y = (point.y + 0.5) * this._cellSize;

        return new Phaser.Math.Vector2( x, y );
    }

    computeWorldPathFromGridPath(gridPath) {

        let worldPathVertices = [];

        for (let i = 0; i < gridPath.length; ++i)
        {
            let gridPoint = gridPath[i];

            let worldPoint = this.convertToWorldCoordsFromGrid({
                x: gridPoint[0],
                y: gridPoint[1]
            });

            worldPathVertices.push(worldPoint);
        }

        let worldPath = new PsyanimPath(worldPathVertices);

        return worldPath;
    }

    _createObstacleFromEntityBounds(entity) {

        // min / max XY values of bounds in world coords
        let minXYWorld = entity.body.bounds.min;
        let maxXYWorld = entity.body.bounds.max;

        let minXY = this.convertToGridFromWorldCoords(minXYWorld);
        let maxXY = this.convertToGridFromWorldCoords(maxXYWorld);

        for (let i = minXY.x; i <= maxXY.x; ++i)
        {
            for (let j = minXY.y; j <= maxXY.y; ++j)
            {
                this._setGridValue(j, i, 1);
            }
        }
    }

    _setGridValue(row, column, value) {

        if (row >= this.rows)
        {
            console.error("ERROR: row index out of bounds: " + row + "... row count = " + this.rows);
        }

        if (column >= this.columns)
        {
            console.error("ERROR: column index out of bounds: " + column + "... column count = " + this.columns);
        }

        this._grid[row][column] = value;
    }
}

class PsyanimPathSegment {

    constructor(p1, p2, distanceToSegment = 0) {

        this._p1 = p1;
        this._p2 = p2;

        this._distanceToSegment = distanceToSegment;

        this._length = p2.clone().subtract(p1).length();
    }

    get distanceToSegment() {
        return this._distanceToSegment;
    }

    get p1() {
        return this._p1;
    }

    get p2() {
        return this._p2;
    }

    get length() {
        return this._length;
    }
