                if (segment.distanceToSegment > parameterDistance)
                {
                    parameterSegment = this._segments[i-1];
                    break;
                }
                else if (segment.distanceToSegment + segment.length > parameterDistance)
                {
                    parameterSegment = this._segments[i];
