        }

        let direction = parameterSegment.p2.clone()
            .subtract(parameterSegment.p1)
            .normalize();

        let distanceFromSegmentStart = parameterDistance
            - parameterSegment.distanceToSegment;

        let position = parameterSegment.p1.clone()
            .add(
