import PsyanimApp from '../../src/core/PsyanimApp.js';

import PsyanimAnimationBaker from '../core/components/utils/PsyanimAnimationBaker.js';

import PsyanimFSMStateRecorder from '../core/components/utils/PsyanimFSMStateRecorder.js';

import PsyanimJsPsychTrialParameter from './PsyanimJsPsychTrialParameter.js';

/**
 *  _PsyanimJsPsychPlugin is a private singleton instance that maintains state across all trials
 *  and handles all communication with PsyanimApp.
 */
class _PsyanimJsPsychPlugin {

    static get Instance() {

        if (_PsyanimJsPsychPlugin._instance == null)
        {
            _PsyanimJsPsychPlugin._instance = new _PsyanimJsPsychPlugin();
        }

        return _PsyanimJsPsychPlugin._instance;
    }

    constructor() {

        console.log("Psyanim jsPsych plugin initialized!");

        this._domElement = document.getElementById('psyanim-app');

        this._currentTrialIndex = 0;
        this._clearConsoleOnNewTrial = true;
    }

    get jsPsych() {

        return this._jsPsych;
    }

    set jsPsych(value) {

        this._jsPsych = value;
    }

    set clearConsoleOnNewTrial(value) {

        this._clearConsoleOnNewTrial = value;
    }

    /**
     * Called by jsPsych at the beginning of every trial
     * @param {*} display_element 
     * @param {*} trial 
     */
    beginNextTrial(display_element, trial) {

        if (this._clearConsoleOnNewTrial)
        {
            console.clear();
        }

        console.log("starting next trial!");

        this._currentTrialJsPsychData = {};

        PsyanimApp.Instance.game.registry.set('psyanimJsPsychPlugin_trialData', this._currentTrialJsPsychData);

        this._jsPsych.pluginAPI.clearAllTimeouts();
        this._jsPsych.pluginAPI.cancelAllKeyboardResponses();

        this._currentTrial = trial;

        // add phaser game canvas to display element
        this._displayElement = display_element;

        if (!document.getElementById(this._domElement.id))
        {
            display_element.appendChild(this._domElement);
        }

        if (this._currentTrial.subtext)
        {
            this._subtextElement = document.createElement('p');
            this._subtextElement.innerHTML = this._currentTrial.subtext;

            this._subtextElement.classList.add('canvas-subtext');

            this._domElement.appendChild(this._subtextElement);
        }

        PsyanimApp.Instance.setCanvasVisible(true);

        // load scene and add parameter-set to game registry
        PsyanimApp.Instance.loadScene(trial.sceneKey);

        /** compute agent names who have trial parameters for this._currentTrial */
        this._trialParameters = this._currentTrial.trialParameters;

        this._trialParameterAgentNames = [...new Set(this._trialParameters
            .filter(p => { 
                // get all agent names who have 'prefab' or 'component' trial parameters
                return p.parameterType === PsyanimJsPsychTrialParameter.Type.PREFAB_PARAMETER || 
                    p.parameterType === PsyanimJsPsychTrialParameter.Type.COMPONENT_PARAMETER 
            })
            .map(p => p.entityName))];

        /** 
         *  compute agents + names to record, considering those who have trial parameters,
         *  instanced entities, entities specified using wildcards...
         */
        this._agentsToRecord = [];

        let agentNamesToRecord = [...new Set(this._trialParameterAgentNames
            .concat(this._currentTrial.agentNamesToRecord))];

        agentNamesToRecord = this._replaceInstancedEntityNames(agentNamesToRecord);

        this._agentNamesToRecord = this._expandWildcardAgentNames(agentNamesToRecord);

        /** setup event handler for after psyanim scene created */
        let trialScene = PsyanimApp.Instance.getSceneByKey(trial.sceneKey);

        trialScene.events.on('create', this._handlePsyanimSceneCreated, this);

        // setup any timeouts that are configured
        if (trial.duration > 0)
        {
            this._jsPsych.pluginAPI.setTimeout(this.endTrial.bind(this), trial.duration);
        }

        // setup events
        if (trial.endTrialOnContact)
        {
            PsyanimApp.Instance.events.on('playerContact', this.endTrial, this);
        }

        if (trial.endTrialOnPlaybackComplete)
        {
            PsyanimApp.Instance.events.on('playbackComplete', this.endTrial, this);
        }

        PsyanimApp.Instance.events.on('psyanim-jspsych-endTrial', this.endTrial, this);

        // setup keyboard inputs
        if (trial.endTrialKeys.length > 0) {

            // let Psyanim process keys related to interactivity, but have jsPsych
            // process keystrokes related to GUI & progressing the experiment trials
            this._jsPsych.pluginAPI.getKeyboardResponse({
                callback_function: this.endTrial.bind(this),
                valid_responses: trial.endTrialKeys, // can also be any keycode, e.g. ' ' for space, 'i', 'u', etc...f
                persist: false
            });
        }
    }

    /**
     * Called by jsPsych at the end of every trial
     */
    endTrial() {

        this._jsPsych.pluginAPI.clearAllTimeouts();
        this._jsPsych.pluginAPI.cancelAllKeyboardResponses();

        // save off documents if document writer configured
        if (this._documentWriter)
        {
            // gather agent metadata
            let agentMetadata = [];

            if (this._agentNamesToRecord)
            {
                this._agentsToRecord.forEach(agent => {

                    let metadata = {
                        name: agent.name,
                        initialPosition: agent.initialPosition,
                        shapeParams: agent.shapeParams,
                        matterOptions: agent.matterOptions
                    };

                    if (this._trialParameterAgentNames.includes(agent.name))
                    {
                        // save off the trial parameters found for this agent
                        metadata.trialParameters = this._trialParameters
                            .filter(p => p.entityName === agent.name)
                            .map(p => {

                                // we don't save off the Entity name
                                let parameter = {
                                    parameterType: p.parameterType,
                                    parameterName: p.parameterName,
                                    parameterValue: p.parameterValue
                                };

                                // we don't save off the componentTypeName unless it's a component parameter
                                if (p.componentTypeName)
                                {
                                    parameter.componentTypeName = p.componentTypeName;
                                }

                                return parameter;
                            });
                    }

                    if (this._agentNamesToRecord.includes(agent.name) && 
                    this._currentTrial.recordAnimationClips)
                    {
                        // save baked animation data
                        let animationBaker = agent.getComponent(PsyanimAnimationBaker);
                        animationBaker.stop();
            
                        metadata.animationClipId = this._documentWriter
                            .addAnimationClip(animationBaker.clip.getData());
                    }

                    // save agent state logs
                    if (this._agentNamesToRecord.includes(agent.name) && 
                        this._currentTrial.recordStateLogs)
                    {
                        let recorder = agent.getComponent(PsyanimFSMStateRecorder);

                        if (recorder)
                        {
                            metadata.stateLogId = this._documentWriter.addAgentStateLog(recorder.data);
                        }
                    }

                    agentMetadata.push(metadata);
                });
            }

            // gather trial metadata
            if (this._currentTrial.saveTrialMetadata)
            {
                let trialMetadata = {

                    sessionID: PsyanimApp.Instance.sessionID,
                    userID: this._userID,
                    experimentName: this._experimentName,
                    trialNumber: this._currentTrialIndex,
                    sceneKey: this._currentTrial.sceneKey,
                    agentMetadata: agentMetadata,
                };
    
                // add scene parameters to trial metadata
                let trialSceneParameters = this._trialParameters
                    .filter(p => p.parameterType === PsyanimJsPsychTrialParameter.Type.SCENE_PARAMETER);
    
                trialSceneParameters.push(new PsyanimJsPsychTrialParameter(
                    PsyanimJsPsychTrialParameter.Type.SCENE_PARAMETER,
                    'canvasSize', {
                        width: PsyanimApp.Instance.currentScene.game.config.width,
                        height: PsyanimApp.Instance.currentScene.game.config.height
                    }
                ));
    
                let color = PsyanimApp.Instance.currentScene.game.config.backgroundColor;
                let colorHex = Phaser.Display.Color.RGBToString(color.r, color.g, color.b, color.a);
    
                trialSceneParameters.push(new PsyanimJsPsychTrialParameter(
                    PsyanimJsPsychTrialParameter.Type.SCENE_PARAMETER,
                    'backgroundColor', colorHex
                ));
    
                trialSceneParameters.push(new PsyanimJsPsychTrialParameter(
                    PsyanimJsPsychTrialParameter.Type.SCENE_PARAMETER,
                    'wrapScreenBoundary', PsyanimApp.Instance.currentScene.screenBoundary.wrap
                ));
    
                trialMetadata.sceneParameters = [];
    
                trialSceneParameters.forEach(p => {
    
                    trialMetadata.sceneParameters.push({
                        parameterType: p.parameterType,
                        parameterName: p.parameterName,
                        parameterValue: p.parameterValue
                    });
                });
    
                this._documentWriter.addExperimentTrialMetadata(trialMetadata);
            }
        }

        // remove any open event subs
        if (this._currentTrial.endTrialOnContact)
        {
            PsyanimApp.Instance.events.off('playerContact', this.endTrial, this);
        }

        if (this._currentTrial.endTrialOnPlaybackComplete)
        {
            PsyanimApp.Instance.events.off('playbackComplete', this.endTrial, this);
        }

        PsyanimApp.Instance.events.off('psyanim-jspsych-endTrial', this.endTrial, this);

        // stop the current scene
        PsyanimApp.Instance.stopCurrentScene();

        // progress to the next trial
        this._currentTrialIndex++;

        // remove phaser game canvas from display element
        PsyanimApp.Instance.setCanvasVisible(false);

        if (this._subtextElement)
        {
            this._domElement.removeChild(this._subtextElement);
            this._subtextElement = null;
        }

        this._displayElement = null;

        this._currentTrial = null;

        // this must be called to end the trial and proceed to the next one!
        this._jsPsych.finishTrial(this._currentTrialJsPsychData);
    }


    /**
     *  Helper methods for computing '_agentNamesToRecord' and '_agentsToRecord' because they now depend on:
     * 
     *      - what the user specifies in the trial's 'agentNamesToRecord' parameter
     *      - what parameters have been modified or specified explicitly to be saved
     *      - whether the user used a wildcard
     *      - how many entity 'instances' are configured
     */

    _replaceInstancedEntityNames(agentNamesToRecord) {

        let sceneDefinition = PsyanimApp.Instance.config.getSceneDefinition(this._currentTrial.sceneKey);

        let instancedEntityBaseNames = [];
        let instancedEntityNames = [];

        for (let i = 0; i < agentNamesToRecord.length; ++i) 
        {
            let agentName = agentNamesToRecord[i];

            // handle wildcard case by taking name root as agent name for instanced agents
            let hasWildcard = agentName.split('*').length > 1;

            if (hasWildcard)
            {
                let nameRoot = agentName.split('*')[0];

                // make sure we ignore duplicates and filter out non-wildcard names that are same as nameRoot
                if (instancedEntityBaseNames.includes(nameRoot))
                {
                    continue;
                }

                agentName = nameRoot;
            }

            let entityDefinition = sceneDefinition.entities.find(e => e.name == agentName);

            if (!entityDefinition)
            {
                console.error('Failed to find an entity definition for agent name: ', agentName);
            }

            if (Object.hasOwn(entityDefinition, 'instances') && entityDefinition.instances > 1)
            {
                let nInstances = entityDefinition.instances;

                for (let j = 0; j < nInstances; ++j)
                {
                    let instanceName = agentName + '_' + (j + 1);

                    if (agentNamesToRecord.includes(instanceName))
                    {
                        console.error('Instance name already exists: ', instanceName);
                    }
                    else
                    {
                        if (!instancedEntityBaseNames.includes(agentName))
                        {
                            instancedEntityBaseNames.push(agentName);
                            instancedEntityBaseNames.push(agentName + '*'); // handle wildcards too
                        }
                        
                        instancedEntityNames.push(instanceName);
                    }
                }
            }
        }

        // remove the original base names used, as well as any wildcard versions
        agentNamesToRecord = agentNamesToRecord.filter(name => !instancedEntityBaseNames.includes(name));

        // add instanced entity names to agentNamesToRecord
        agentNamesToRecord.push(...instancedEntityNames);

        return agentNamesToRecord;
    }

    _expandWildcardAgentNames(agentNamesToRecord) {

        let sceneDefinition = PsyanimApp.Instance.config.getSceneDefinition(this._currentTrial.sceneKey);

        let wildcardNames = [];
        let expandedNames = [];

        for (let i = 0; i < agentNamesToRecord.length; ++i)
        {
            let agentName = agentNamesToRecord[i];

            // handle wildcard case by taking name root as agent name for instanced agents
            let hasWildcard = agentName.split('*').length > 1;

            if (hasWildcard)
            {
                let nameRoot = agentName.split('*')[0];

                let entityNames = sceneDefinition.entities
                    .filter(e => e.name.startsWith(nameRoot))
                    .map(e => e.name);

                expandedNames.push(...entityNames);
                wildcardNames.push(agentName);
            }
        }

        // remove the wildcards
        agentNamesToRecord = agentNamesToRecord.filter(name => !wildcardNames.includes(name));

        // add expanded names
        agentNamesToRecord.push(...expandedNames);

        return agentNamesToRecord;
    }

    _getAgentsByName(agentNames) {

        let agents = [];

        agentNames.forEach(name => {

            let agent = null;

            let hasWildcard = name.split('*').length > 1;

            if (hasWildcard) // handle wildcard case
            {
                let nameRoot = name.split('*')[0];

                let entityNames = PsyanimApp.Instance.currentScene.getAllEntityNames()
                    .filter(entityName => entityName.startsWith(nameRoot));

                entityNames.forEach(entityName => {
                    
                    agent = PsyanimApp.Instance.currentScene.getEntityByName(entityName);

                    if (agent)
                    {
                        agents.push(agent);
                    }
                    else
                    {
                        console.error("ERROR: invalid agent name to record: " + name);
                    }
                });
            }
            else // handle single agent case
            {
                agent = PsyanimApp.Instance.currentScene.getEntityByName(name);

                if (agent)
                {
                    agents.push(agent);
                }
                else
                {
                    console.error("ERROR in scene '", PsyanimApp.Instance.currentSceneKey, 
                        "': invalid agent name: " + name, 
                        ", valid names = ", PsyanimApp.Instance.currentScene.getAllEntityNames());
                }
            }
        });

        return agents;
    }

    /**
     *  Handlers for after trial has begun and PsyanimScene creation has completed
     */

    _handlePsyanimSceneCreated(scene) {

        if (this._currentTrial.recordAnimationClips && this._agentNamesToRecord.length > 0)
        {
            this._setupAnimationBaking();
        }

        scene.events.off('create', this._handlePsyanimSceneCreated, this);
    }

    _setupAnimationBaking() {

        this._agentsToRecord = this._getAgentsByName(this._agentNamesToRecord);

        if (this._agentsToRecord.length > 0)
        {
            this._agentsToRecord.forEach(agent => {

                let baker = agent.addComponent(PsyanimAnimationBaker);

                let animationParameters = this._currentTrial.animationParameters
                    .filter(p => p.entityName === agent.name);

                baker.addAnimationParameters(animationParameters);

                baker.start();
            });
        }
    }
}

export default class PsyanimJsPsychPlugin {

    static setDocumentWriter(writer) {

        _PsyanimJsPsychPlugin.Instance._documentWriter = writer;
    }

    static setExperimentName(experimentName) {

        _PsyanimJsPsychPlugin.Instance._experimentName = experimentName;
    }

    static setUserID(userID) {

        _PsyanimJsPsychPlugin.Instance._userID = userID;
    }

    static setClearConsoleOnNewTrial(clear) {

        _PsyanimJsPsychPlugin.Instance.clearConsoleOnNewTrial = clear;
    }

    constructor(jsPsych) {

        _PsyanimJsPsychPlugin.Instance.jsPsych = jsPsych;
    }

    trial(display_element, trial) {

        _PsyanimJsPsychPlugin.Instance.beginNextTrial(display_element, trial);;
    }
}

PsyanimJsPsychPlugin.info = {
    
    name: 'psyanim-jsPsych-plugin',
    parameters: {

        sceneKey: {
            type: ParameterType.HTML_STRING,
            default: undefined
        },

        endTrialKeys: {
            type: ParameterType.KEYS,
            default: ['enter']
        },

        duration: {
            type: ParameterType.FLOAT,
            default: -1.0
        },

        endTrialOnContact: {
            type: ParameterType.BOOL,
            default: false
        },

        endTrialOnPlaybackComplete: {

            type: ParameterType.BOOL,
            default: false
        },

        saveTrialMetadata: {
            type: ParameterType.BOOL,
            default: true
        },

        agentNamesToRecord: {
            type: ParameterType.OBJECT,
            default: []
        },

        recordAnimationClips: {
            type: ParameterType.BOOL,
            default: true
        },

        recordStateLogs: {
            type: ParameterType.BOOL,
            default: true
        },

        subtext: {
            type: ParameterType.STRING,
            default: ''
        },

        trialParameters: {
            type: ParameterType.OBJECT,
            default: []
        },

        animationParameters: {
            type: ParameterType.OBJECT,
            default: []
        }
    }
};

_PsyanimJsPsychPlugin._instance = null;

import PsyanimApp from '../core/PsyanimApp.js';
import PsyanimJsPsychPlugin from './PsyanimJsPsychPlugin.js';
import PsyanimJsPsychTrialParameter from './PsyanimJsPsychTrialParameter.js';

import {
    PsyanimUtils,
    PsyanimDebug
} from 'psyanim-utils';

/**
 * PsyanimJsPsychTrial class provides an API for building up a trial object as required
 * by the PsyanimJsPsychPlugin, for ease-of-use and to serve as a form of documentation itself.
 * 
 * It is highly recommended to use this class to generate the jsPsych trial object for your scene, 
 * rather than building it up manually.
 * 
 * It is also recommmended that you construct a new PsyanimJsPsychTrial object for each trial
 * in your jsPsych experiment.
 */
export default class PsyanimJsPsychTrial {

    /**
     * @param {Object} sceneDefinitionTemplate - the Psyanim scene definition which will be used as a template for this trial
     * @param {string} [sceneKey] - if provided, the scene key used for this trial will be overriden with this field's value
     */
    constructor(sceneDefinitionTemplate, sceneKey = null) {

        this._sceneDefinition = PsyanimUtils.cloneSceneDefinition(sceneDefinitionTemplate);

        // TODO: verify scene key is unique

        if (sceneKey)
        {
            this._sceneDefinition.key = sceneKey;
        }

        PsyanimApp.Instance.config.registerScene(this._sceneDefinition);

        this._validEntityParameters = ['initialPosition', 'shapeParams', 'matterOptions', 'instances'];

        this._validShapeParameters = [
            'shapeType', 'radius', 'base', 'altitude', 'width', 'height', 'depth', 'color'
        ];

        this._validMatterOptions = [
            'isSensor', 'isStatic'
        ];

        // setup other plugin parameters
        this._endTrialKeys = ['enter'];
        this._duration = -1.0;
        this._endTrialOnContact = false;
        this._endTrialOnPlaybackComplete = false;
        this._agentNamesToRecord = [];
        this._saveTrialMetadata = true;
        this._recordAnimationClips = true;
        this._recordStateLogs = true;
        this._subtext = '';
        this._jsPsychData = {};
        this._extensions = [];

        // trial parameters that the user can decide to save
        this._trialParametersToSave = [];

        // animation parameters that the user can decide to save
        this._animationParameters = [];
    }

    /**
     * Returns the `jsPsych trial object` corresponding to this trial's configuration for your `jsPsych timeline`.
     * 
     * @return {Object} - jsPsych trial object, to be added to jsPsych timeline
     */
    get jsPsychTrialDefinition() {

        return {
            type: PsyanimJsPsychPlugin,
            sceneKey: this.sceneKey,
            duration: this.duration,
            endTrialOnContact: this.endTrialOnContact,
            endTrialOnPlaybackComplete: this.endTrialOnPlaybackComplete,
            agentNamesToRecord: this.agentNamesToRecord,
            saveTrialMetadata: this._saveTrialMetadata,
            recordAnimationClips: this.recordAnimationClips,
            recordStateLogs: this.recordStateLogs,
            subtext: this._subtext,
            trialParameters: this._trialParametersToSave,
            animationParameters: this._animationParameters,
            data: this._jsPsychData,
            extensions: this._extensions
        };
    }

    /**
     * The scene key associated with this trial
     */
    get sceneKey() {

        return this._sceneDefinition.key;
    }

    /**
     * The `duration` parameter for a trial determines the maximum amount of time the trial should last.
     * 
     * @return {number} - trial duration parameter
     */
    get duration() {

        return this._duration;
    }

    /**
     * The `duration` parameter for a trial determines the maximum amount of time the trial should last.
     * 
     * @param {number} - trial duration parameter
     */
    set duration(value) {

        this._duration = value;
    }

    /**
     * 
     */
    get endTrialOnContact() {

        return this._endTrialOnContact;
    }

    set endTrialOnContact(value) {

        this._endTrialOnContact = value;
    }

    get endTrialOnPlaybackComplete() {

        return this._endTrialOnPlaybackComplete;
    }

    set endTrialOnPlaybackComplete(value) {

        this._endTrialOnPlaybackComplete = value;
    }

    get agentNamesToRecord() {

        return this._agentNamesToRecord;
    }

    get jsPsychData() {

        return this._jsPsychData;
    }

    set jsPsychData(value) {

        this._jsPsychData = value;
    }

    addExtension(type, params = {}) {

        // TODO: can we do some error-checking?

        this._extensions.push({ 
            type: type,
            params: params
        });
    }

    addAgentNamesToRecord(namesArray) {

        if (!Array.isArray(namesArray))
        {
            PsyanimDebug.error("'namesArray' must be an array of strings!");
            return;
        }

        this._agentNamesToRecord = this._agentNamesToRecord.concat(namesArray);
    }

    get saveTrialMetadata() {

        return this._saveTrialMetadata;
    }

    set saveTrialMetadata(value) {

        this._saveTrialMetadata = value;
    }

    get recordAnimationClips() {

        return this._recordAnimationClips;
    }

    set recordAnimationClips(value) {

        this._recordAnimationClips = value;
    }

    get recordStateLogs() {

        return this._recordStateLogs;
    }

    set recordStateLogs(value) {

        this._recordStateLogs = value;
    }

    get subtext() {

        return this._subtext;
    }

    set subtext(value) {

        this._subtext = value;
    }

    _getEntityDefinition(entityName) {

        let entityDefinition =  this._sceneDefinition.entities.find(e => e.name === entityName);

        if (!entityDefinition)
        {
            PsyanimDebug.error('No entity definition for entity named: ' + entityName);
        }

        return entityDefinition;
    }

    setEntityParameter(entityName, parameterName, value) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!this._validEntityParameters.includes(parameterName))
        {
            PsyanimDebug.error("Parameter name '" + parameterName + "' is not a valid entity parameter. " + 
                "Valid parameters are: " + JSON.stringify(this._validEntityParameters));

            return;
        }

        entityDefinition[parameterName] = value;
    }

    setEntityShapeParameter(entityName, parameterName, value) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!Object.hasOwn(entityDefinition, 'shapeParams'))
        {
            entityDefinition['shapeParams'] = {};
        }

        let shapeParams = entityDefinition['shapeParams'];

        if (!this._validShapeParameters.includes(parameterName))
        {
            PsyanimDebug.error("Shape parameter name '" + parameterName + "' is not a valid shape parameter. " + 
                "Valid parameters are: " + JSON.stringify(this._validShapeParameters));

            return;
        }

        shapeParams[parameterName] = value;
    }

    setEntityMatterOptions(entityName, parameterName, value) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!Object.hasOwn(entityDefinition, 'matterOptions'))
        {
            entityDefinition['matterOptions'] = {};
        }

        let matterOptions = entityDefinition['matterOptions'];

        if (!this._validMatterOptions.includes(parameterName))
        {
            PsyanimDebug.error("Matter options parameter name '" + parameterName + "' is not a valid parameter. " + 
                "Valid parameters are: " + JSON.stringify(this._validMatterOptions));

            return;
        }

        matterOptions[parameterName] = value;
    }

    setPrefabParameter(entityName, parameterName, value, saveParameter = true) {

        let prefabParams = this.getPrefabParameters(entityName);

        prefabParams[parameterName] = value;

        if (saveParameter)
        {
            this.savePrefabParameter(entityName, parameterName);
        }
    }

    setComponentParameter(entityName, componentType, parameterName, value, saveParameter = true) {

        let componentParameters = this.getComponentParameters(entityName, componentType);

        componentParameters[parameterName] = value;

        if (saveParameter)
        {
            this.saveComponentParameter(entityName, componentType, parameterName);
        }
    }

    getSceneParameter(parameterName) {

        // TODO: validate parameter name

        return this._sceneDefinition[parameterName];
    }

    getEntityParameter(entityName, parameterName) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!this._validEntityParameters.includes(parameterName))
        {
            PsyanimDebug.error("Parameter name '" + parameterName + "' is not a valid entity parameter. " + 
                "Valid parameters are: " + JSON.stringify(this._validEntityParameters));

            return;
        }

        return entityDefinition[parameterName];
    }

    getPrefabParameters(entityName) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!Object.hasOwn(entityDefinition, 'prefab'))
        {
            PsyanimDebug.error('No prefab field found for entity named: ' + entityName);
            return;
        }

        let prefabDefinition = entityDefinition['prefab'];

        if (!Object.hasOwn(prefabDefinition, 'params'))
        {
            prefabDefinition['params'] = {};
        }

        return prefabDefinition['params'];
    }

    getComponentParameters(entityName, componentType) {

        let entityDefinition = this._getEntityDefinition(entityName);

        if (!Object.hasOwn(entityDefinition, 'components'))
        {
            PsyanimDebug.error('No components found for entity named: ' + entityName);
            return;
        }

        let componentDefinitions = entityDefinition['components'];

        let componentDefinition = componentDefinitions
            .find(c => c.type == componentType);

        if (!componentDefinition)
        {
            PsyanimDebug.error('Failed to find component type: ' + componentType.name);
            return;
        }

        if (!Object.hasOwn(componentDefinition, 'params'))
        {
            componentDefinition['params'] = {};
        }

        return componentDefinition['params'];
    }

    saveSceneParameter(parameterName) {

        let parameterValue = this.getSceneParameter(parameterName);

        let trialParameter = new PsyanimJsPsychTrialParameter(
            PsyanimJsPsychTrialParameter.Type.SCENE_PARAMETER,
            parameterName, parameterValue
        );

        if (!this._trialParametersToSave.find(p => p.is(trialParameter)))
        {
            this._trialParametersToSave.push(trialParameter);
        }
    }

    savePrefabParameter(entityName, parameterName) {

        let prefabParameters = this.getPrefabParameters(entityName);
        
        let parameterValue = prefabParameters[parameterName];

        let trialParameter = new PsyanimJsPsychTrialParameter(
            PsyanimJsPsychTrialParameter.Type.PREFAB_PARAMETER,
            parameterName, parameterValue, entityName
        );

        if (!this._trialParametersToSave.find(p => p.is(trialParameter)))
        {
            this._trialParametersToSave.push(trialParameter);
        }
    }

    recordAnimationParameter(entityName, componentType, key, name) {

        this._animationParameters.push({
            entityName: entityName,

            // had to wrap this value in an object or jsPsych would try to mess with it
            componentInfo: { type: componentType }, 

            key: key,
            name: name
        });
    }

    saveComponentParameter(entityName, componentType, parameterName) {

        let componentParameters = this.getComponentParameters(entityName, componentType);

        let parameterValue = componentParameters[parameterName];

        let trialParameter = new PsyanimJsPsychTrialParameter(
            PsyanimJsPsychTrialParameter.Type.COMPONENT_PARAMETER,
            parameterName, parameterValue, entityName, componentType
        );

        if (!this._trialParametersToSave.find(p => p.is(trialParameter)))
        {
            this._trialParametersToSave.push(trialParameter);
        }
    }
}

import PsyanimApp from '../core/PsyanimApp.js';
import PsyanimComponent from '../core/PsyanimComponent.js';

export default class PsyanimJsPsychTrialLoader extends PsyanimComponent {

    trialInfo;
    documentReader;

    constructor(entity) {

        super(entity);
    }

    afterCreate() {

        super.afterCreate();

        this._getTrialMetadata();
    }

    _getTrialMetadata() {

        let trialIDs = this.trialInfo.map(info => info.trialID);

        this.documentReader.getTrialMetadataByIdAsync(trialIDs)
            .then((docs) => {

                this._trialMetadata = docs;

                this._getAnimationClips();
            })
            .catch((errorMessage) => {
                console.error(errorMessage);
            });
    }
    
    _getAnimationClips() {

        let clipIDs = [];

        this._trialMetadata
            .forEach(docData => {

                let doc = docData.data;

                let agentMetadata = doc.agentMetadata;

                agentMetadata.forEach(agent => {
                    
                    if (Object.hasOwn(agent, 'animationClipId') && 
                        !clipIDs.includes(agent.animationClipId))
                    {
                        clipIDs.push(agent.animationClipId);
                    }
                })
            });

        this.documentReader.getAnimationClipsByIdAsync(clipIDs)
            .then((clipData) => {

                this._animationData = clipData;

                this._saveDataAndEndTrial();
            })
            .catch((errorMessage) => {
                console.error(errorMessage);
            });
    }

    _saveDataAndEndTrial() {

        this.scene.registry.set('psyanim_trialMetadata', this._trialMetadata);
        this.scene.registry.set('psyanim_animationData', this._animationData);

        console.log('Successfully loaded experiment data!');

        // load the next trial
        PsyanimApp.Instance.events.emit('psyanim-jspsych-endTrial');
    }

    update(t, dt) {
        
        super.update(t, dt);
    }
}

export default class PsyanimJsPsychTrialParameter {

    parameterType;
    entityName;
    parameterName;
    parameterValue;
    componentTypeName;

    constructor(parameterType, parameterName, parameterValue, entityName = '', componentType = '') {

        this.parameterType = parameterType;
        this.entityName = entityName;
        this.parameterName = parameterName;
        this.parameterValue = parameterValue;
        
        if (this.parameterType == PsyanimJsPsychTrialParameter.Type.COMPONENT_PARAMETER)
        {
            if (!componentType)
            {
                PsyanimDebug.error('component parameter has no component type defined!');
                return;
            }

            this.componentTypeName = componentType.name;
        }
    }

    is(trialParameter) {

        return this.parameterType == trialParameter.parameterType &&
            this.entityName == trialParameter.entityName &&
            this.parameterName == trialParameter.parameterName &&
            this.componentTypeName == trialParameter.componentTypeName;
    }
}

PsyanimJsPsychTrialParameter.Type = {
    SCENE_PARAMETER: 'scene',
    PREFAB_PARAMETER: 'prefab',
    COMPONENT_PARAMETER: 'component'
};

import PsyanimComponent from '../core/PsyanimComponent.js';

import {
    PsyanimDebug,
    PsyanimUtils
} from 'psyanim-utils';

export default class PsyanimJsPsychTrialSelector extends PsyanimComponent {

    trialInfo;

    randomizeTrialOrder;

    constructor(entity) {

        super(entity);

        this.randomizeTrialOrder = false;

        this._currentTrialJsPsychData = this.scene.registry.get('psyanimJsPsychPlugin_trialData');
    }

    _getTrialData() {

        let trialIDs = this.trialInfo.map(info => info.trialID);

        if (trialIDs.length == 0)
        {
            PsyanimDebug.error('No trial IDs configured for selector!');
        }

        this._usedTrialIDs = this.scene.registry.get('psyanimTrialSelector_usedTrialIDs');

        if (this.randomizeTrialOrder)
        {
            if (!this._usedTrialIDs)
            {
                // reference to '_usedTrialIDs' *is* the reference pointed at by the phaser registry
                this._usedTrialIDs = [];
                this.scene.registry.set('psyanimTrialSelector_usedTrialIDs', this._usedTrialIDs);
            }    
        }
        else
        {
            if (!this.scene.registry.has('psyanimTrialSelector_nextTrialIndex'))
            {
                this.scene.registry.set('psyanimTrialSelector_nextTrialIndex', 0);
            }
        }

        this._trialMetadata = this.scene.registry.get('psyanim_trialMetadata')
            .filter(t => trialIDs.includes(t.id));

        this._animationData = this.scene.registry.get('psyanim_animationData');
    }

    _getNextTrialIDRandomized() {

        let availableTrialMetadata = this._trialMetadata.filter(t => {
            return !this._usedTrialIDs.includes(t.id)
        });

        let trialMetadataIndex = PsyanimUtils.getRandomInt(0, availableTrialMetadata.length - 1);

        return availableTrialMetadata[trialMetadataIndex].id;
    }

    _getNextTrialID() {

        let nextTrialIndex = this.scene.registry.get('psyanimTrialSelector_nextTrialIndex');

        let trialID = this._trialMetadata[nextTrialIndex].id;

        this.scene.registry.set('psyanimTrialSelector_nextTrialIndex', nextTrialIndex + 1);

        return trialID;
    }

    getTrialData() {

        if (!this._trialMetadata)
        {
            // lazy load it b.c. experiment manager calls it from afterCreate()
            this._getTrialData();
        }

        let trialID = null;

        if (this.randomizeTrialOrder)
        {
            trialID = this._getNextTrialIDRandomized();

            this._usedTrialIDs.push(trialID);
        }
        else
        {
            trialID = this._getNextTrialID();
        }

        this._currentTrialJsPsychData['trialID'] = trialID;

        PsyanimDebug.log('playing back trial ID: ' + trialID);

        let trialMetadata = this._trialMetadata.find(doc => doc.id === trialID).data;

        let trialPlaybackInfo = this.trialInfo.find(info => info.trialID === trialID);

        return {
            trialPlaybackInfo: trialPlaybackInfo,
            trialMetadata: trialMetadata,
            animationClipData: this._animationData
        };
    }

    update(t, dt) {
        
        super.update(t, dt);
    }
}

